$color-background-secondary: stencilColor('color-background-secondary');
$color-nav: stencilColor('color-nav');
$color-nav-active: stencilColor('color-nav-active');
$color-nav-background: stencilColor('color-nav-background');
$font-body: $font-primary;

$color-accent: stencilColor('color-accent');
$color-border: stencilColor('color-border');
$color-link: stencilColor('color-link');
$color-link-hover: stencilColor('color-link-hover');

$color-input: stencilColor('color-input');

$heading-font: stencilString('font-secondary');
$heading-weight: 700;
$heading-color: #333;

$section-title-color: stencilColor('color-section-title');

$meta-font: stencilString('font-secondary');;
$meta-weight: 400;
$color-meta: stencilColor('color-meta');
$color-bold: stencilColor('color-bold');

$button-font: stencilString('font-primary');;
$button-weight: 400;

$border-size: stencilNumber("border-size", "px");
$main-nav-spacing: stencilNumber("global-main-nav-spacing", "px");

$color-button: stencilColor('color-button');
$color-button-background: stencilColor('color-button-background');
$color-button-background-hover: stencilColor('color-button-background-hover');

$color-button-secondary: stencilColor('color-button-secondary');
$color-button-secondary-background: stencilColor('color-button-secondary-background');

$color-button-tertiary: stencilColor('color-button-tertiary');
$color-button-tertiary-background: stencilColor('color-button-tertiary-background');

$color-topbar-background: stencilColor('color-topbar-background');
$color-topbar-text: stencilColor('color-topbar-text');

$color-mini-cart-background: stencilColor('color-mini-cart-background');

$color-carousel-background: stencilColor('color-carousel-background');
$color-carousel-navigation: stencilColor('color-carousel-navigation');
$color-carousel-title: stencilColor('color-carousel-title');
$color-carousel-text: stencilColor('color-carousel-text');

$color-modal-background: stencilColor('color-modal-background');

$color-footer-background: stencilColor('color-footer-background');
$color-footer-text: stencilColor('color-footer-text');

$color-collection-item-overlay: stencilColor('color-collection-item-overlay');

/* scss-combine:general/variables */
// Breakpoints
$breakpoint-xs: 480px;
$breakpoint-s: 768px;
$breakpoint-m: 1024px;
$breakpoint-l: 1440px;
$breakpoint-xl: 1920px;

// Grid
$max-width: 1230px;
$font-size: 15px;
$gutter: 15px;
$double-gutter: $gutter * 2;
$border-radius: 4px;

$susy: (
  columns: 12,
  gutters: 0.25,
  debug: (
    image: hide,
    output: background,
  )
);

// Easing
$ease-out-quad: cubic-bezier(0.250, 0.460, 0.450, 0.940);
$ease-out-back: cubic-bezier(0.175, 0.885, 0.320, 1.275);

// Transitions
$transition-navbar: 0.2s linear;

// Alert Colours
$color-info: #00529B;
$color-success: #8BC53F;
$color-warning: #F3B603;
$color-error: #FF5E5D;
$color-alert-text: #fff;

// Colors
$color-topbar-sep: contrast(#000, 100%);

// Fonts
$roboto: 'Roboto Condensed', sans-serif;
$roboto-slab: 'Roboto Slab', serif;

// Measurements
$topbar-height: rem-calc(56px);
$navbar-height: rem-calc(100px);
$compressed-navbar-height: rem-calc(56);
$expanded-nav-height: $topbar-height + $navbar-height;
$modal-width-narrow: rem-calc(684px);
$section-padding: rem-calc(80px);
$blog-index-width: rem-calc(645px);
$mini-cart-width: 400px;

// Z Indices
$nav-mobile-z: 1000;

// Core
$account-light-color: $color-background;
$account-accent-color: $color-body;
$account-border-color: $color-border;
$account-link-color: $color-accent;
$account-gutter: $gutter;
$account-font-size: $font-size;


// Helpers
/* scss-combine:general/mixins */
/*----------  Layout  ----------*/


// Media Queries
@mixin media($max: false, $min: false) {
  @if $max {
    @if $min {
      @media screen and (max-width: $max - 1) and (min-width: $min) { @content; }
    } @else {
      @media screen and (max-width: $max - 1) { @content; }
    }
  } @else {
    @media screen and (min-width: $min) { @content; }
  }
}

// Maintain Aspect Ratio
// @include maintain-ratio(16 9);
@mixin maintain-ratio($ratio: 1 1) {
  @if length($ratio) < 2 or length($ratio) > 2 {
    @warn "$ratio must be a list with two values.";
  }

  $width: 100%;
  $height: percentage(nth($ratio, 2) / nth($ratio, 1));

  width: $width;
  height: 0;
  padding-bottom: $height;
}

@mixin layout-group($gutter: 0, $gutter-vertical: null) {
  @if $gutter-vertical == null {
    $gutter-vertical: $gutter;
  }

  display: table;

  @if $gutter == 0 {
    width: 100%;
  } @else {
    width: calc(100% + #{$gutter * 2});
    margin-left: $gutter * -1;
    border-spacing: $gutter $gutter-vertical;
  }
}

@mixin layout-group-item {
  display: table-cell;
}

// Centerer
@mixin centerer {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}


/*----------  Icons  ----------*/

// Circle
@mixin icon-circle($size: 18px, $color: $color-meta) {
  display: inline-block;
  width: $size;
  height: $size;
  overflow: hidden;
  padding: 0;
  line-height: $size - 4px;
  text-align: center;
  border-radius: 50%;
  border: 2px solid $color;
  color: $color;
}

@mixin icon-arrow-down($fill-color) {
  @extend %background-position-svg;
  background-image: svg-url('<svg fill="#{$fill-color}" version="1.1" xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 32 32"><path d="M6.514 6.232l9.487 9.597 9.487-9.597c1.491-1.491 3.904-1.491 5.395 0 1.483 1.507 1.491 3.944 0 5.451l-12.19 12.306c-1.483 1.491-3.904 1.491-5.395 0l-12.182-12.306c-1.489-1.507-1.489-3.944 0.002-5.451 1.491-1.491 3.904-1.491 5.395 0z"></path></svg>');
}

@mixin icon-check-mark($fill-color) {
  @extend %background-position-svg;
  background-image: svg-url('<svg fill="#{$fill-color}" version="1.1" xmlns="http://www.w3.org/2000/svg" width="44" height="32" viewBox="0 0 44 32"><path d="M41.186 1.67c-1.936-1.937-5.075-1.937-7.011 0l-17.239 17.237-8.472-8.472c-1.935-1.937-5.076-1.937-7.011 0s-1.936 5.075 0 7.011l11.978 11.979c0.968 0.969 2.237 1.452 3.506 1.452s2.538-0.483 3.506-1.452l20.744-20.744c1.937-1.936 1.937-5.075 0.001-7.011z"></path></svg>');
}

@mixin icon-email($fill-color) {
  @extend %background-position-svg;
  background-image: svg-url('<svg fill="#{$fill-color}" version="1.1" xmlns="http://www.w3.org/2000/svg" width="43" height="32" viewBox="0 0 43 32"><path d="M40 0h-37.333c-1.475 0-2.667 1.192-2.667 2.667v26.667c0 1.475 1.192 2.667 2.667 2.667h37.333c1.475 0 2.667-1.192 2.667-2.667v-26.667c0-1.475-1.192-2.667-2.667-2.667v0zM37.333 5.333v0.176l-16 12.445-16-12.445v-0.176h32zM5.333 26.667v-14.4l14.363 11.171c0.48 0.376 1.059 0.563 1.637 0.563s1.157-0.187 1.637-0.563l14.363-11.171v14.4h-32z"></path></svg>');
}

@mixin icon-password($fill-color) {
  @extend %background-position-svg;
  background-image: svg-url('<svg fill="#{$fill-color}" version="1.1" xmlns="http://www.w3.org/2000/svg" width="24" height="32" viewBox="0 0 24 32"><path d="M22 12h-2v-4c0-4.418-3.582-8-8-8s-8 3.582-8 8v4h-2c-1.106 0-2 0.894-2 2v16c0 1.106 0.894 2 2 2h20c1.106 0 2-0.894 2-2v-16c0-1.106-0.894-2-2-2v0zM8 8c0-2.208 1.792-4 4-4s4 1.792 4 4v4h-8v-4zM4 16h16v12h-16v-12z"></path><path d="M12 24c1.106 0 2-0.894 2-2s-0.894-2-2-2c-1.106 0-2 0.894-2 2s0.894 2 2 2z"></path></svg>');
}

@mixin icon-close($fill-color) {
  @extend %background-position-svg;
  background-image: svg-url('<svg fill="#{$fill-color}" width="15" height="15" viewBox="0 0 15 15" xmlns="http://www.w3.org/2000/svg"><title>close</title><path d="M15 1.73L13.52.25 7.89 5.88 2.26.25.778 1.73l5.63 5.63-5.63 5.63 1.482 1.482 5.63-5.63 5.63 5.63 1.48-1.48-5.63-5.63L15 1.73"/></svg>');
}

/*----------  Theme Editor Settings  ----------*/

@mixin font-switcher($use-primary-font) {
  @if ($use-primary-font) {
    font-family: $font-primary;
  } @else {
    font-family: $font-secondary;
  }
}

@mixin white-text($background) {
  @if lightness($background) < 33 {
    color: contrast($background, 100%);
  }
}

/* scss-combine:general/extends */
/*----------  Text treatments  ----------*/

%body-text-treatment {
  font-family: $font-body;
  color: $color-body;
  font-size: $font-size;
  line-height: 1.666;
}

%heading-text-treatment {
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-family: $heading-font;
  font-weight: $heading-weight;
  color: $heading-color;
}

%page-header-treatment {
  background-color: $color-background-secondary;
  padding: rem-calc(40px) rem-calc(62px);
  text-align: center;
}

%section-title-text-treatment {
  @extend h3;
  text-align: center;
  color: $section-title-color;
}

%meta-text-treatment {
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-family: $meta-font;
  font-weight: $meta-weight;
  color: $color-meta;
}

%top-bar-font {
  text-transform: uppercase;
  font-size: 13px;
  font-weight: 300;
}

%link-text-treatment {
  padding-bottom: 2px;
  font-weight: 400;
  text-transform: uppercase;
  letter-spacing: 0.077em;
  font-size: rem-calc(13);
  text-decoration: underline;

  @if ($font-secondary == "Roboto Condensed") {
    font-family: $font-secondary;
  }

  &:hover {
    text-decoration: none;
    color: $color-link-hover;
  }
}

%list-style {
  padding-left: 1.25em;
  margin-bottom: 2em;

  li {
    margin-bottom: 1em;
  }
}

/*----------  Layout  ----------*/

%wrapper {
  @extend %wrapper-padding;
  margin-right: auto;
  margin-left: auto;
  max-width: $max-width;

  /* Solves border-box issue with max-width in IE8 */
  .lt-ie9 & { width: $max-width; }
}

%wrapper-padding {
  padding-left: 30px - $gutter;
  padding-right: 30px - $gutter;
}

%full-width {
  padding-left: 30px;
  padding-right: 30px;
}

%ghost-center {
  content: " ";
  display: inline-block;
  height: 100%;
  margin-right: -0.25em;
  vertical-align: middle;
}

/*----------  Forms  ----------*/

%form-base-style {
  @include container;
  background-color: $color-background-secondary;
  padding: 50px $gutter;
  margin-top: 56px;

  @include media($min: $breakpoint-s) {
    padding: 50px;
  }
}

/*----------  Utils  ----------*/

%top-bar-sep {
  position: absolute;
  bottom: 0;
  display: inline-block;
  width: 1px;
  height: 25px;
  margin-left: 18px;
  vertical-align: top;
  content: '';
  pointer-events: none;
  background-color: $color-border;
  transform: translateY(-50%);

  @if (lightness($color-topbar-background) < 70) {
    opacity: 0.3;
  }
}

%fill-parent {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

%inline-middle {
  display: inline-block;
  vertical-align: middle;
}

%center-x {
  position: relative;
  left: 50%;
  transform: translateX(-50%);
}

%background-position-svg {
  background-position: 50% 50%;
  background-repeat: no-repeat;
  background-size: contain;
}

%background-position-img {
  background-position: 50% 50%;
  background-repeat: no-repeat;
  background-size: cover;
}

%placeholder {
  padding-top: 2px;
  letter-spacing: 0.1em;
  color: lighten($color-input, 6%);
  text-transform: uppercase;
  font-size: rem-calc(13);
}

/*----------  Button  ----------*/

%button-inverse {
  &:hover {
    box-shadow: 0 0 0 $border-size $color-button inset;
  }
}

%button-secondary-inverse {
  color: lighten($color-button-secondary, 100%);
  box-shadow: 0 0 0 $border-size lighten($color-button-secondary, 100%) inset;

  &:hover {
    box-shadow: 0 0 0 $border-size transparent inset;
  }
}


// Core
/* scss-combine:core/account */
// Default Variables

$account-link-color: #888 !default;
$account-light-color: #fff !default;
$account-accent-color: #5dc5e3 !default;
$account-border-color: #e4e4e4 !default;
$account-gutter: 30px !default;
$account-font-size: 15px !default;
$account-border-radius: 0 !default;
$account-text-color: #5f5f5f !default;

$account-card-background-color: #f5f5f5 !default;

$giftcard-nav-use-default-styles: true !default;

$account-breakpoint-x-small: 400px !default; // the account table columns change to full-width
$account-breakpoint-small: 620px !default; // menu changes from full-width to column
$account-breakpoint-medium: 900px !default; // menu changes from 35% to 25% width

// Internal Variables

$account-font-size-heading: $account-font-size + 2 !default;

$account-text-color-light: lighten($account-text-color,20%) !default;

$account-gutter-inner: $account-gutter / 2 !default;

$auth-gutter: $account-gutter !default;
$auth-font-size: $account-font-size !default;

// Imports

/* scss-combine:account/components/layout */
.account-wrapper {
  @extend %clearfix;
  font-size: $account-font-size;
}

.account-nav,
.account-body {
  vertical-align: top;
}

.account-body {
  @media screen and (min-width: $account-breakpoint-small) {
    float: right;
    width: 65%;
    padding-left: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 75%;
  }

  > :first-child {
    margin-top: 0;
  }

  .account-nav-hidden & {
    float: none;
    width: 100%;
    padding-left: 0;
    margin-left: 0;
  }
}

.account-nav {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    float: left;
    width: 35%;
    padding-right: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 25%;
  }
}

/* scss-combine:account/components/forms */
.account-wrapper {
  .form-label-text {
    display: inline-block;
    width: 100%;
    margin-bottom: 5px;
  }

  .form-field-checkbox,
  .form-field-radio {
    .form-label-text { width: auto; }
  }

  .form-field {
    margin-bottom: $account-gutter-inner;
  }

  .form-input {
    width: 100%;

    &.account-form-half {
      width: 50%;
    }
  }

  .form-actions {
    clear: both;
  }
}

.account-form-column-container {
  @extend %clearfix;

  .form-field {
    @media screen and (min-width: 480px) {
      float: left;
      width: 50%;

      &:nth-child(odd) { padding-right: $account-gutter-inner; }
      &:nth-child(even) { padding-left: $account-gutter-inner; }
    }
  }
}

/* scss-combine:account/components/table */
.account-wrapper {
  table {
    width: 100%;
    margin-top: $account-gutter-inner;
    margin-bottom: $account-gutter-inner;
  }

  td,
  th {
    padding: $account-gutter-inner;
  }
}

.account-table-tight {
  th,
  td {
    padding: 10px 0;
    border-bottom: 1px solid $account-border-color;
  }
}

/* scss-combine:account/components/list */
// Layout
// Each list item has 3 sections: thumbnail, body, and badge.

.account-items {
  margin-bottom: $account-gutter;
}

.account-item {
  position: relative;
  display: table;
  width: 100%;
  border-bottom: 1px solid $account-border-color;

  &:first-of-type {
    border-top: 1px solid $account-border-color;
  }
}

.account-item-thumbnail,
.account-item-body,
.account-item-badge,
.account-item-actions {
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-x-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Link
// Makes the entire item clickable

.account-item-link {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
}

// Thumbnail

.account-item-thumbnail {
  min-width: 100px;

  img {
    height: auto;
    max-width: 100px;
  }
}

// Body

.account-item-body {
  width: 100%;

  @media screen and (min-width: $account-breakpoint-x-small) {
    .account-item-thumbnail + & {
      padding-left: $account-gutter-inner;
    }
  }
}

.account-item-title {
  margin-top: 3px;
  font-size: $account-font-size;

  a {
    color: inherit;
  }
}

.account-item-title-large {
  font-size: $account-font-size * 1.2;
}

.account-item-description {
  margin-top: 0;
  margin-bottom: 5px;
  color: $account-text-color;
}

.account-item-details {
  @extend %clearfix;
  font-size: $account-font-size;
  color: $account-text-color;
}

.account-item-detail {
  float: left;
  margin-top: $account-gutter-inner;
  margin-right: $account-gutter;
}

//scss-lint:disable VariableForProperty
.account-item-detail-title {
  display: block;
  margin-top: 0;
  margin-bottom: 3px;
  font-size: 12px;
  font-weight: normal;
  letter-spacing: 1px;
  color: mix($account-light-color,$account-text-color,60%);
  text-transform: uppercase;
}

// Badge

.account-item-badge,
.account-item-actions {
  white-space: nowrap;
}

/* scss-combine:account/components/header */
.account-header {
  display: table;
  width: 100%;
  margin-bottom: $account-gutter-inner;
}

.account-heading,
.account-toolbar {
  display: table-cell;
  vertical-align: baseline;
}

.account-toolbar {
  text-align: right;

  * {
    display: inline;
  }
}

.account-tool {
  position: relative;
  z-index: 10;
  display: inline-block;
  padding: 0;
  background: transparent;
  margin-left: 10px;
  cursor: pointer;
  color: inherit;
  border: 0;

  &:hover { color: $account-link-color; }

  &:focus { outline: 0; }
}

/* scss-combine:account/components/navigation */
.account-nav-list {
  padding: 0;
  margin-bottom: $account-gutter-inner;
  list-style: none;
  border: 1px solid $account-border-color;
  border-radius: $account-border-radius;
}

.account-nav-item {
  color: $account-link-color;
  border-top: 1px solid $account-border-color;

  &:first-child {
    border-top: 0;
  }

  &.is-active {
    color: $account-accent-color;
    border-left: 3px solid $account-accent-color;
  }
}

.account-nav-link {
  display: block;
  padding: 15px;
  color: inherit;
}

.account-nav-badge {
  float: right;
}

/* scss-combine:account/components/pagination */
.account-wrapper .pagination {
  margin-top: $account-gutter-inner;
}

/* scss-combine:account/components/card */
%account-card-style {
  background: $account-card-background-color;

  .form-input {
    background-color: $account-light-color;
  }
}

.account-card {
  @extend %account-card-style;
  padding: $account-gutter-inner;
  margin: $account-gutter-inner 0;
  border-radius: $account-border-radius;
}

/* scss-combine:account/components/toggle */
// Set the initial toggle state to hidden
.account-toggle-hidden {
  display: none;
}

/* scss-combine:account/components/status */
// Status
// A label used to mark the status of an item

.account-status {
  padding: 6px 10px 5px;
  margin: 0;
  font-size: 12px;
  font-weight: normal;
  line-height: 1;
  letter-spacing: 1px;
  color: $account-light-color;
  text-align: center;
  text-transform: uppercase;
  background: #8e8e8e; // TODO: Colors for each status
}

/* scss-combine:account/components/shipments */
// Shipments
// A list of shipments made for an order. Shown on the Order Details page.

.account-shipments {
  margin-bottom: $account-gutter;
}

.account-shipments-table {
  th,
  td {
    padding: 10px 0;
    text-align: left;
  }

  th {
    padding: $account-gutter-inner 0 0;
    border-top: 1px solid $account-border-color;
  }

  // Override default margins
  .account-wrapper & {
    margin-top: 0;
    margin-bottom: 0;
  }
}


/* scss-combine:account/pages/address */
// Address List

.account-page-add-address {
  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-address-detail {
  display: block;
}

// Add Address

.account-address-form {
  padding-top: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-medium) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1){
        clear: left;
      }
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}

