// Module overrides
/* scss-combine:modules/dropdown */
.dropdown {
  position: relative;
}

.dropdown-panel {
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 1000;
  display: none;
  max-height: calc(100vh - 112px);
  min-width: 100%;
  padding: rem-calc(10);
  overflow: auto;

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    opacity: 0;
    transform: translateY(-10px);
    transition: opacity 0.5s ease, transform 0.3s ease;
  }

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }
}

.icon-arrow-down {
  display: inline-block;
  margin-top: -0.03em;
  margin-left: 0.5em;
  vertical-align: middle;
  transition: transform 0.3s ease;

  .dropdown-open & {
    transform: rotate(-180deg);
  }

  .nav-mobile-panel-parent .nav-mobile-item-parent & {
    margin-top: -0.1rem;
  }
}

// Dropdown tier Styles
.tier-toggle {
  padding: rem-calc(5) 0 !important; //override padding from .main-nav-bar dropdown selector
  background: transparent;
}

.tier-open > a {
  color: contrast($color-nav-active, 100%);
}

.tier-panel {
  position: static;
  display: none;
  padding: 0 0 0 rem-calc(10);
  margin: 0;
  overflow: hidden;
  background: transparent;

  &.tier-open { display: block; }

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    opacity: 0;
    transition: opacity 0.5s ease;
  }

  &.visible {
    opacity: 1;
  }

  a {
    white-space: nowrap;

    .main-dropdown & {
      line-height: 30px;
    }
  }
}

/* scss-combine:modules/modal */
.modal-overlay {
  background-color: rgba($color-modal-background, 0.95);
}

.modal {
  margin-bottom: 5vw;
  width: 90%;
  max-width: $max-width;
  padding: $gutter;

  .loaded & {
    background-color: #fff;
  }

   &.modal-quick-shop {
    top: 5vw;
  }

  .icon-spinner {
    display: block;
    width: 30px;
    height: 30px;
    margin: 0 auto;
  }
}

.button-modal-close {
  position: absolute;
  top: 19px;
  right: 20px;
  color: contrast($color-modal-background, 100%);
  background: none;

  .visible & {
    position: fixed;
  }

  &:hover {
    color: $color-accent;
  }

  .icon-close {
    width: 16px;
    height: 16px;
  }
}

.modal-close {
  position: absolute;
  top: -18px;
  right: -18px;
  display: block;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  text-align: center;
  line-height: 36px;
  background-color: $color-body;
  color: contrast($color-body, 100%, 70);
  transition: all 0.3s ease;

  &:hover {
    background-color: $color-accent;
    color: contrast($color-body, 100%, 70);
  }

  .icon-close {
    @include centerer;
  }
}

/* scss-combine:modules/tabs */
.tabs {
  margin-bottom: 55px;
  font-size: 0;
  text-align: center;
}

.tab-title {
  padding: 0;
  font-size: $font-size;
  border: 2px solid $color-border;

  &:hover {
    color: contrast($color-nav-active, 100%);
    background-color: $color-accent;
    border-color: $color-accent;
  }

  &.active {
    color: contrast($color-nav-active, 100%, 60);
    background-color: $color-nav-active;
    border-color: $color-nav-active;
  }

  &:not(:first-child) {
    margin-left: -1px;
  }

  &:first-child {
    border-radius: $border-radius;

    a { cursor: default; }

    &:not(:last-child) {
      border-radius: $border-radius 0 0 $border-radius;

      a { cursor: pointer; }
    }
  }

  &:last-child:not(:first-child) {
    border-radius: 0 $border-radius $border-radius 0;
  }

  a {
    display: block;
    min-width: 190px;
    padding: 14px;
    color: inherit;

    &:hover {
      color: inherit;
    }
  }
}

.tabs-content {
  position: relative;

  .icon-spinner {
    position: absolute;
    top: 0;
    left: 50%;
    margin-left: -10px;
  }
}

.tab-content-panel {
  &.active ~ .icon-spinner {
    display: none;
  }
}

/* scss-combine:modules/baguetteBox */
/*!
 * baguetteBox.js
 * @author  feimosi
 * @version 1.4.1
 * @url https://github.com/feimosi/baguetteBox.js
 */

// scss-lint:disable QualifyingElement, IdSelector, SelectorFormat

%arrow-button {
  top: calc(50% - 30px);
  width: 44px;
  height: 60px;
}

#baguetteBox-overlay {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000000;
  display: none;
  width: 100%;
  height: 100%;
  overflow: hidden;
  background-color: rgba($color-modal-background, 0.98);
  opacity: 0;
  transition: opacity 0.5s ease;

  &.visible { opacity: 1; }

  .full-image {
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    text-align: center;

    figure {
      display: inline;
      height: 100%; // Opera 12 image stretching fix
      margin: 0;
    }

    img {
      display: inline-block;
      width: auto;
      height: auto;
      max-width: 100%;
      max-height: 90%;
      vertical-align: middle;
    }

    figcaption {
      position: absolute;
      bottom: 0;
      display: block;
      width: 100%;
      padding-bottom: 16px;
      font-weight: 300;
      line-height: 1.8;
      text-align: center;
      white-space: normal;
    }

    &:before {
      display: inline-block;
      width: 1px;
      height: 50%;
      margin-right: -1px;
      content: "";
    }
  }
}

#baguetteBox-slider {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  white-space: nowrap;
  transition: left 0.4s ease, transform 0.4s ease;

  &.bounce-from-right {
    animation: bounceFromRight 0.4s ease-out;
  }

  &.bounce-from-left {
    animation: bounceFromLeft 0.4s ease-out;
  }
}

@keyframes bounceFromRight{
  0% { margin-left: 0; }
  50% { margin-left: -30px; }
  100% { margin-left: 0; }
}

@keyframes bounceFromLeft{
  0% { margin-left: 0; }
  50% { margin-left: 30px; }
  100% { margin-left: 0; }
}

.baguetteBox-button {
  position: absolute;
  padding: 0;
  margin: 0;
  font-size: 1.6em;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  border-radius: 15%;
  outline: none;
  transition: background-color 0.4s ease;

  &#next-button {
    @extend %arrow-button;
    right: 2%;
  }

  &#previous-button {
    @extend %arrow-button;
    left: 2%;
  }

  &#close-button {
    top: 20px;
    right: 20px;
    width: 30px;
    height: 30px;
  }

  // Firefox fix
  svg {
    position: absolute;
    top: 0;
    left: 0;

    g {
      stroke: contrast($color-modal-background, 100%);
    }
  }
}

// http://tobiasahlin.com/spinkit/
.spinner:not(.button) {
  position: absolute;
  top: 50%;
  left: 50%;
  display: inline-block;
  width: 40px;
  height: 40px;
  margin-top: -20px;
  margin-left: -20px;
}

.double-bounce1,
.double-bounce2 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  opacity: 0.6;
  animation: bounce 2s infinite ease-in-out;
}

.double-bounce2 { animation-delay: -1s; }

@keyframes bounce {
  0%, 100% { transform: scale(0); }

  50% { transform: scale(1); }
}

/* scss-combine:modules/meganav */
.mega-nav-variant-container {
  display: block;
