    text-align: left;
    padding: 5px;

    &:hover {
      cursor: pointer;
    }

    a {
      color: contrast($color-nav-active, 100%);
    }
  }
}

.mega-nav-root-list {
  @include span(3);
  border-right: 1px solid contrast($color-topbar-background, 10%);
  display: block;
  font-size: rem-calc(18px);
  font-weight: 600;
  height: 460px;
  letter-spacing: 0.077em;
  max-height: 460px;
  overflow: hidden;
  overflow-y: auto;
  position: relative;
  text-transform: uppercase;

  li {
    display: block;

    a { letter-spacing: normal; }

    &.active a { color: $color-accent; }

    &:hover a {
      color: contrast($color-nav-active, 100%);
      text-decoration: underline;
    }
  }
}

.mega-nav-child-container {
  display: block;
  float: left;
  max-width: span(9 of 12);
  max-height: rem-calc(400px);
  padding-left: rem-calc(30px);
}

.mega-nav-child-list-container {
  display: none;

  &.animating,
  &.visible {
    display: block;
  }

  // Prevent jumping because 2 menus are showing momentarily
  &.animating {
    position: absolute;
  }

  &.animating-in,
  &.animating-out {
    opacity: 0;
    transform: translateY(-10px);
    transition: opacity 0.5s ease, transform 0.3s ease;
  }

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }
}

.mega-nav-child-header {
  display: block;
  font-family: $roboto;
  color: contrast($color-topbar-background, 100%);
  margin-top: 0;
  margin-bottom: rem-calc(10px);
  padding: 0;
  text-align: left;
  width: 100%;
}

.mega-nav-child-list-back {
  display: block;
  height: rem-calc(20px);
  margin-bottom: rem-calc(15px);
  text-align: left;
  font-size: 12px;
  margin-left: -7px;

  svg {
    margin-top: -3px;
    vertical-align: middle;
    transform: rotate(90deg) !important;
    color: contrast($color-topbar-background, 100%);

    path { transition: all 0.2s $ease-out-quad; }
  }

  a {
    height: 100%;
    color: contrast($color-topbar-background, 100%);
    text-transform: uppercase;
    text-decoration: underline;
    transition: all 0.2s $ease-out-quad;
  }

  &:hover {
    a{
      text-decoration: none;
      color: $color-link-hover;
    }

    svg path { fill: $color-link-hover; }
  }
}

.mega-nav-child-list-inner-container {
  display: flex;
  height: rem-calc(360px);
}

.mega-nav-child-list {
  display: flex;
  max-height: 100%;
  flex-wrap: wrap;
  flex-direction: column;
  float: left;
  font-family: $roboto-slab;
  font-size: 13px;

  .mega-nav-child-item {
    align-self: baseline;
    margin-right: rem-calc(60px);
    min-height: 0;
    min-width: 0;
    padding: 5px 5px 5px 0;
    text-align: left;

    a {
      color: $color-nav;
      font-size: rem-calc(16px);
      font-weight: lighter;
      padding: 0;
    }

    &:hover {
      > a { color: $color-link-hover; }
      .mega-nav-arrow svg path { fill: $color-link-hover; }
    }
  }
}

.mega-nav-arrow {
  display: inline-block;
  height: 100%;
  margin-left: rem-calc(2px);
  margin-top: 3px;
  vertical-align: top;
  width: rem-calc(13px);

  .icon-arrow-down {
    transform: rotate(-90deg);
    vertical-align: middle;

    path { fill: $color-nav; }
  }
}

/* scss-combine:modules/carousel */
/*----------  Carousel  ----------*/

.carousel {
  cursor: ew-resize;
  cursor: grab;

  .js & .carousel-item {
    opacity: 0;
    transition: opacity 0.6s ease;
  }

  .js &.slick-initialized .carousel-item {
    opacity: 1;
  }

  .container {
    position: relative;

    @include media($max: $breakpoint-s) {
      padding: 0;
    }
  }
}

.carousel-item {
  width: 100%;

  > a {
    display: block;
  }
}

.carousel-item-image {
  @include maintain-ratio(2.4 1);
  background-size: cover;

  .ratio-natural & {
    height: auto;
    padding-bottom: 0;
  }

  .ratio-3-1 & { @include maintain-ratio(3 1); }

  .ratio-16-9 & { @include maintain-ratio(16 9); }
}

.slick-slide {
  .home-carousel-image  {
    @extend .sr-only;

    .ratio-natural & {
      position: static !important; // override !important in .sr-only
      display: block;
      width: 100%;
      height: auto;
    }
  }

  .product-images-container & {
    height: auto !important; // override height from JS causing slides to be given incorrect height
  }
}

.carousel-item-info {
  padding: 37px 0;
  text-align: center;
  opacity: 0;
  transform: translate(0, 100%);
  transition: all 0.2s ease-out;
  background-color: rgba($color-carousel-background, 0.8);

  @include media($min: $breakpoint-s) {
    position: absolute;
    left: $gutter * 3;
    bottom: 0;
    width: 380px;
    padding: 25px 31px 32px;
    text-align: left;
  }

  &.show {
    opacity: 1;
    transform: translate(0, 0);
  }

  .carousel-item-title {
    margin: 0;
    font-family: $font-secondary;
    font-weight: 400;
    font-size: 40px;
    color: $color-carousel-title;
  }

  .carousel-item-caption {
    font-size: 15px;
    color: rgba($color-carousel-text, 0.75);
  }

  .carousel-item-link {
    font-size: 13px;
    color: $color-accent;
  }
}

.carousel-navigation-item {
  position: absolute;
  top: 50%;
  z-index: 1;
  padding: 1rem;
  color: $color-carousel-navigation;
  cursor: pointer;
  transform: translateY(-50%);

  @include media($max: $breakpoint-s) {
    display: none !important;
  }

  &.previous { left: 0; }
  &.next { right: 0; }

  .icon {
    height: 24px;
    width: 15px;
  }
}

.slick-dots {
  position: absolute;
  // Calculate the ratio of 2.4:1 and convert it to a viewport width
  top: (1 / 2.4 * 100) * 1vw;
  left: 50%;
  transform: translateX(-50%) translateY(-34px);
  list-style: none;
  margin: 0 0 30px 0;
  line-height: 1;
  text-align: center;

  @include media($min: $breakpoint-s) {
    top: auto;
  }

  // Calculate the ratio of 3:1 and convert it to a viewport width
  .ratio-3-1 & { top: (1 / 3 * 100) * 1vw; }

  // Calculate the ratio of 16:9 and convert it to a viewport width
  .ratio-16-9 & { top: (9 / 16 * 100) * 1vw; }

  li {
    display: inline-block;
    width: 16px;
    height: 16px;
    border: 2px solid $color-carousel-navigation;
    border-radius: 50%;
    margin: 0 4px;
    cursor: pointer;
    transition: all 0.3s ease;

    &:hover {
      background-color: $color-accent;
      border-color: $color-accent;
    }
  }

  .slick-active {
    border-color: $color-accent;
  }

  button {
    display: none;
  }
}


// General
/* scss-combine:general/general */
/* General */

body {
  @extend %body-text-treatment;
  background-color: $color-background;

  &.scroll-locked { overflow: hidden; }
}

/* Links */

a {
  color: $color-link;
  transition: color 0.2s $ease-out-quad;

  &:hover {
    color: $color-link-hover;
  }
}

.link {
  @extend %link-text-treatment;
}

/* Headers */

h1,
h2,
h3,
h4,
h5,
h6 {
  line-height: 1.25;

  a {
    color: inherit;

    &:hover {
      color: $color-accent;
    }
  }
}

h1,
h3,
h5 {
  font-weight: 400;
}

h1,
h3,
h5 {
  @if ($font-secondary == "Roboto Condensed") {
    text-transform: uppercase;
  }

  font-family: $font-secondary, sans-serif;
  color: $color-bold;
}

h2,
h4 {
  font-family: $font-primary;
  font-weight: 300;
  text-transform: none;
}

h1 { font-size: rem-calc(36); }
h2 { font-size: rem-calc(28); }
h3 { font-size: rem-calc(24); }
h4 { font-size: rem-calc(20); }
h5 {
  @if ($font-secondary == "Roboto Condensed") {
    font-weight: 700;
  }

  font-size: rem-calc(17);
}

h6 {
  @if ($font-secondary == "Roboto Condensed") {
    font-family: $font-secondary;
  }

  text-transform: uppercase;
  font-size: rem-calc(13);
  font-weight: 700;
  letter-spacing: 0.0625em;
  color: $color-bold;
}

/* Utility classes */

.text-center {
  text-align: center;
}

.sr-only {
  @extend .show-for-sr;
}

.hidden {
  display: none;
}

/* Basic elements */

hr {
  background: $color-border;
  height: 1px;
}

cite {
  color: $color-meta;
}

img {
  max-width: 100%;
  height: auto;

  // Hide lazy-loading images while they are loading
  &:not([src]) {
    visibility: hidden;
  }
}

svg {
  fill: currentColor;
}

.list-bulleted {
  margin: 1em 0 1em 1.5em;
  list-style: disc;
}

/* scss-combine:general/button */
/* Buttons */
button {
  border: 0;
}

.button {
  padding: 1.0625em;
  font-family: $button-font;
  font-size: 16px;
  font-weight: $button-weight;
  line-height: 1.3125; // 21/16
  color: $color-button;
  background-color: $color-button-background;
  border-radius: 4px;
  box-shadow: 0 0 0 $border-size transparent inset;
  transition: all 0.2s $ease-out-quad;

  &:hover {
    color: $color-button;
    background-color: $color-button-background-hover;
  }

  &.inverse {
    @extend %button-inverse;
  }

  &.button-wide {
    padding-left: 3em;
    padding-right: 3em;
  }

  &.button-small {
    padding: 0.25em 0.5em;
    font-size: 13px;
  }

  &.button-short {
    padding: 0.75em 1.5em;
  }

  &.button-secondary {
    color: $color-button-secondary;
    background: $color-button-secondary-background;
    box-shadow: 0 0 0 $border-size $color-button-secondary inset;

    &:hover {
      color: lighten($color-button-secondary, 100%);
      background-color: $color-button-secondary;
    }

    &.inverse {
      @extend %button-secondary-inverse;
    }
  }

  &.button-tertiary {
    color: $color-button-tertiary;
    background: $color-button-tertiary-background;

    &:hover {
      color: $color-button-tertiary-background;
      box-shadow: 0 0 0 $border-size $color-button-tertiary-background inset;
      background-color: transparent;
    }
  }

  &:disabled,
  &.button-disabled {
    cursor: not-allowed;
    opacity: 0.6;
  }

  &.spinner {
    position: relative;

    &.progress {
      .button-text {
        opacity: 0.1;
      }
    }
  }

  .icon-spinner {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    margin: -10px 0 0 -10px;
  }
}

.button-group {
  @extend %clearfix;
  display: inline-block;
}

.button-group-item {
  position: relative;
  float: left;
  height: 45px;
  min-width: 45px;
  padding: 12px;
  font-family: $font-secondary;
  font-size: rem-calc(13px);
  line-height: 1;
  color: $color-meta;
  text-align: center;
  background-color: transparent;
  border: 2px solid $color-border;
  transition: all 0.3s ease;

  + .button-group-item {
    margin-left: -2px;
  }

  &:hover {
    background-color: $color-accent;
    border-color: $color-accent;
    color: contrast($color-nav-active, 100%);
  }

  &:first-child {
    border-radius: $border-radius 0 0 $border-radius;
  }

  &:last-child {
    border-radius: 0 $border-radius $border-radius 0;
  }

  &.active {
    z-index: 1;
    color: contrast($color-nav-active, 100%, 60);
    background-color: $color-nav-active;
    border-color: $color-nav-active;
  }

  &.disabled,
  &.active {
    pointer-events: none;
    cursor: default;
  }

  .icon-grid-view {
    width: 16px;
    height: 16px;
  }

  .icon-list-view {
    width: 17px;
    height: 16px;
  }
}

/* scss-combine:general/form */
/*----------  Forms  ----------*/

.form-field {
  margin-bottom: rem-calc(28);
}

.form-label {
  font-weight: 400;

  &.checkbox {
    .form-label-text {
      margin-bottom: 0;
    }
  }
}

.form-input {
  display: block;
  padding: rem-calc(19 17 18);
  color: $color-input;
  border: 0;
  border-radius: $border-radius;
  transition: border-color 0.2s $ease-out-quad;

  @if ($font-secondary == "Roboto Condensed") {
    font-family: $font-secondary;
  }

  &:not([type="radio"]):not([type="checkbox"]):not(.quantity-input) {
    box-shadow: 0 0 0 $border-size $color-border inset;
  }

  &:not(textarea):not([type="radio"]):not([type="checkbox"]) {
    height: rem-calc(55);
  }

  &::placeholder {
    @extend %placeholder;
  }

  &:focus {
    outline: none;
  }

  &.form-input-short {
    padding: rem-calc(14 17 13);
    height: rem-calc(45);
  }
}

.form-radio,
.form-checkbox {
  display: inline-block;
  vertical-align: middle;

  + .form-label-text {
    display: inline-block;
    font-weight: 300;
    margin-bottom: rem-calc(10);
    margin-left: rem-calc(10);
  }
}

.form-select-wrapper {
  position: relative;
  display: block;

  &:after {
    @include icon-arrow-down($color-meta);
    position: absolute;
    top: 50%;
    right: 20px;
    display: inline-block;
    width: 9px;
    height: 6px;
    margin-top: -3px;
    pointer-events: none;
    content: "";
  }

  .form-select { width: 100%; }
}

.form-select {
  height: rem-calc(55);
  padding: 0 rem-calc(36) 0 rem-calc(17);
  background-color: $color-background;
  appearance: none;

  // For IE 10+
  &::-ms-expand { display: none; }

  &.form-input-short {
    height: rem-calc(45);
    padding: 0 rem-calc(36) 0 rem-calc(17);
  }
}

.form-label-text,
.form-field-title {
  display: block;
  margin: 0 rem-calc(10) rem-calc(8px) 0;
  font-weight: normal;
}

.form-field-title {
  .required-text {
    display: none;

    .form-required & {
      display: inline-block;
    }
  }
}

.input-group {
  @include layout-group;
}

.input-group-item {
  @include layout-group-item;
  vertical-align: top;

  &:first-child {
    .button {
      border-radius: $border-radius 0 0 $border-radius;
      margin-right: -2px;
    }
  }

  &:last-child {
    .button {
      border-radius: 0 $border-radius $border-radius 0;
      margin-left: -2px;
    }
  }

  &.form-field {
    width: 99%;
  }

  .form-input {
    width: 100%;
  }
}

// ---------------------------------------------------------------- */
// Date Field Components

.form-field-date {
  @extend %clearfix;

  .form-select-wrapper { float: left; }

  .form-date-year { width: 44%; }

  .form-date-month {
    width: 25%;
    margin-right: 3%;
    margin-left: 3%;
  }

  .form-date-day { width: 25%; }
}

/*----------  Form Errors  ----------*/

.form-error {
  color: $color-error;
  border: 1px solid $color-error;

  &::placeholder {
    color: lighten($color-error,30%);
  }
}

.form-error-message {
  color: $color-error;
}

.form-inline-message {
  display: block;
  margin-top: rem-calc(10);
  clear: left;

  .form-field-invalid & {
    color: $color-error;
  }
}

/* scss-combine:general/table */
/* Tables */

table {
  color: $color-body;
  border-color: $color-border;
}

th {
  @extend %heading-text-treatment;
  color: $color-input;
}

/* scss-combine:general/alerts */
.alert {
  position: relative;
  padding: rem-calc(16) rem-calc(32) rem-calc(16) rem-calc(16);
  font-size: rem-calc(14);
  color: $color-alert-text;
  text-align: center;
  transition: opacity 0.3s $ease-out-quad;

  &.dismissed { opacity: 0; }

  &.hidden,
  &.alert-dynamic { display: none; }

  &.animating,
  &.visible {
    display: block;
  }

  // initial state for enter transition
  &.animating-in,
  &.animating-out {
    opacity: 0;
    transition: opacity 0.3s $ease-out-quad;
  }

  // final states for both in and out
  &.visible {
    transform: translateX(0);
    opacity: 1;
  }

  .alert-dismiss {
    position: absolute;
    top: rem-calc(24);
    right: rem-calc(16);
    width: rem-calc(12);
    height: rem-calc(12);
    line-height: 0;
    color: transparent;
    cursor: pointer;

    &:hover { opacity: 1; }
  }

  &.alert-success,
  &.success {
    background-color: $color-success;

    .alert-dismiss { @include icon-close(darken($color-success, 20%)); }
  }

  &.alert-error,
  &.error {
    background-color: $color-error;

    .alert-dismiss { @include icon-close(darken($color-error, 30%)); }
  }

  &.alert-info,
  &.neutral {
    background-color: $color-info;

    .alert-dismiss { @include icon-close(darken($color-info, 30%)); }
  }

  a:not(.alert-dismiss) {
    color: $color-alert-text;
    text-decoration: underline;

    &:hover {
      text-decoration: none;
    }
  }
}

/* scss-combine:general/global */
/*----------  Spinner  ----------*/

.loading-overlay {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
  background-color: rgba($color-background, 0.8);

  .icon-spinner {
    position: fixed;
    top: 50%;
    left: 50%;
    margin: -10px 0 0 -10px;
  }
}


/*----------  Animations  ----------*/

.icon-spinner {
  width: 20px;
  height: 20px;
  animation: spin 0.75s linear infinite;
}

@keyframes spin {
  100% { transform: rotate(360deg); }
}


/*----------  Page  ----------*/

.page-header {
  @extend %page-header-treatment;

  .page-title {
    margin: 0 auto;
  }
}

.section-title {
  @extend %section-title-text-treatment;
  margin: 0 0 2.083em; //50/24
  font-size: 24px;
}

.page-content,
.product-description,
.blog-content {
  ul,
  ol {
    @extend %list-style;
  }

  ul {
    list-style: disc;
  }

  a {
    color: $color-accent;

    &:hover {
      text-decoration: underline;
    }
  }

  img {
    margin-bottom: 2em;
  }

  .feedflare {
    img {
      margin-bottom: 0;
    }
  }

  .page-feed {
    h6 {
      margin-top: 0;
    }

    li {
      margin-bottom: 0;
    }
  }
}


/*----------  Pagination  ----------*/

.pagination-wrapper {
  margin: $section-padding 0;
  text-align: center;
}

.pagination {
  .icon {
    position: relative;
    top: rem-calc(2);
    width: rem-calc(12);
    height: rem-calc(10);
  }

  .pagination-page-link {
    line-height: rem-calc(17px);
  }
}


/*----------  Newsletter Signup  ----------*/

.newsletter-signup {
  background-color: $color-background-secondary;

  .container {
    max-width: 616px;
  }

  .subscription-form {
    text-align: center;

    @include media($min: $breakpoint-xs) {
      display: flex;
      align-items: flex-start;
      justify-content: center;
    }
  }

  .form-field {
    display: block;
    width: 100%;
    margin-bottom: 30px;

    @include media($min: $breakpoint-xs) {
      display: inline-block;
      flex: 2;
      width: auto;
      margin: 0 30px 0 0;
    }
  }

  .form-input {
    width: 100%;
  }

  .form-inline-message {
    text-align: left;
  }
}


/*----------  Banners  ----------*/

.banners {
  @extend %clearfix;
  padding: 0 $gutter;
  text-align: center;
  background: $color-background-secondary;
}

.banners-bottom {
  background: $color-background;
  border-top: 1px solid $color-border;
  border-bottom: 1px solid $color-border;
}

.banner {
  max-width: $max-width;
  padding: $gutter 0;
  margin: 0 auto;
  text-align: center;
  border-bottom: 1px solid $color-border;

  // override default rte colors
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 { color: inherit; }

  a {
    text-decoration: underline;

    &:hover {
      color: $color-link;
    }
  }

  &:last-child { border-bottom: 0; }
}

/* scss-combine:general/images */
.replaced-image {
  display: block;
  min-width: 1rem;
  min-height: 1rem;
  overflow: hidden;
  background: {
    position: center center;
    repeat: no-repeat;
    size: contain;
  }

  .thumbnail-cover &,
  .post & {
    background-size: cover;
  }

  img,
  &.lazy-image img {
    vertical-align: middle;  // Avoid baseline / descender padding
    visibility: hidden;   // Image is replaced
  }
}

.lazy-image {
  position: relative;

  .spinner {
    position: absolute;
    top: 50%;
    left: 50%;
    margin-top: -0.5rem;
    margin-left: -0.5rem;
  }
}

[class*="ratio-"]:not(.ratio-any) {
  img {
    @extend .sr-only;
  }
}

.ratio-any {
  img {
    width: 100%;
  }
}

.ratio-16-9 {
  @include maintain-ratio(16 9);
}

.ratio-4-3 {
  @include maintain-ratio(4 3);
}

.ratio-1-1 {
  @include maintain-ratio(1 1);
}

.ratio-3-1 {
  @include maintain-ratio(3 1);
}

.ratio-3-4 {
  @include maintain-ratio(3 4);
}


// Components
/* scss-combine:components/mini-cart */
.mini-cart {
  position: fixed;
  top: 0;
  right: -400px;
  width: 400px;
  height: 100%;
  background-color: $color-mini-cart-background;
  transition: transform .3s ease;

  @if ($color-mini-cart-background == $color-background) {
    box-shadow: 1px 0 0 0 $color-border inset;
  }

  .no-js & { overflow: auto; }

  .button-checkout {
    @extend %button-inverse;
  }
}

.mini-cart-inner {
  position: relative;
  top: 0;
}

.mini-cart-top-bar {
  @include white-text($color-mini-cart-background);

  .button-cart-close {
    position: absolute;
    top: rem-calc(40);
    right: rem-calc(25);
    padding: 0;
    background: none;

    .icon-close {
      &:hover {
        color: $color-accent;
      }
    }
  }
}

.mini-cart-contents {
  > .icon-spinner {
    display: block;
    margin: rem-calc(20) auto;
  }
}

.mini-cart-title {
  @include white-text($color-mini-cart-background);
  font-size: 18px;
  text-align: center;
  font-family: $font-primary;
  font-weight: 300;
  text-transform: none;
  margin: 0;
  padding: rem-calc(40) 0;
}

.mini-cart-empty {
  @include white-text($color-mini-cart-background);
  text-align: center;
}

.mini-cart-item {
  @include white-text($color-mini-cart-background);
  position: relative;
  transition: background-color 0.3s ease;

  &:not(:last-of-type) {
    &:after {
      display: block;
      width: calc(100% - 50px);
      height: 1px;
      margin-left: 25px;
      background-color: rgba(contrast($color-mini-cart-background, 100%), 0.05);
      content: "";
    }
  }

  &:hover {
    background-color: darken($color-mini-cart-background, 5%);

    &:after {
      background-color: darken($color-mini-cart-background, 5%);
    }

    .mini-cart-item-actions {
      opacity: 1;
    }
  }

  &.removing {
    .mini-cart-item-wrap {
      pointer-events: none;
      opacity: 0.2;
    }
  }

  a {
    color: inherit;
  }

  .icon-spinner {
    position: absolute;
    top: 25px;
    right: 25px;
  }
}

.mini-cart-item-wrap {
  @include layout-group();
  padding: 25px;

  & > * {
    @include layout-group-item();
  }

  .giftcard-image {
    width: 75px;
  }
}

.mini-cart-item-thumb {
  width: 75px;
  padding-bottom: 75px;
  background-position: center center;
  background-size: contain;
  background-repeat: no-repeat;

  .thumbnail-cover & {
    background-size: cover;
  }
}

.mini-cart-item-title {
  font-family: $font-secondary, sans-serif;
  font-weight: bold;
  font-size: 17px;
  text-transform: uppercase;
}

.mini-cart-item-options {
  padding-top: 2px;
  padding-bottom: 2px;

  .option-value {
    margin-right: 4px;
    color: $color-body;

    &:not(:last-child):after {
      content: ",";
    }
  }
}

.mini-cart-item-description {
  vertical-align: middle;
  padding-left: 20px;
  width: 90%;
}

.mini-cart-item-actions {
  font-size: 12px;
  opacity: 0;
  transition: opacity 0.3s ease;

  .link {
    margin-right: 14px;
  }
}

.mini-cart-item-price {
  text-align: right;
  vertical-align: bottom;
  opacity: 0.6;

  .price-discounted { text-decoration: line-through; }
}

.mini-cart-footer {
  padding: 25px;
  border-top: 1px solid rgba(contrast($color-mini-cart-background, 100%), 0.05);

  .mini-cart-subtotal {
    float: right;
    font-size: 111%;
    vertical-align: middle;
  }
}

.mini-cart-footer-title {
  color: contrast($color-mini-cart-background, 100%);
}

.mini-cart-actions {
  .button {
    width: calc(50% - 5px);

    & + .button { float: right; }
  }

  .button-secondary {
    @include white-text($color-mini-cart-background);

    @if lightness($color-mini-cart-background) < 33 {
      box-shadow: 0 0 0 2px contrast($color-mini-cart-background, 100%) inset;
    }

    &:hover {
      box-shadow: 0 0 0 2px $color-button-secondary inset;
    }
  }
}

/* scss-combine:components/search */
/*----------  Search Form  ----------*/

.search-form {
  position: relative;
  max-width: 250px;
  margin: rem-calc(22px) auto 0;

  .search-input {
    width: 100%;
  }

  .button-search-submit {
    position: absolute;
    top: 50%;
    right: $gutter;
    line-height: 0;
    color: $color-meta;
    background: none;
    transform: translateY(-50%);
  }

  .icon-search {
    width: 24px;
    height: 24px;
  }

  .form-inline-message {
    position: absolute;
    bottom: rem-calc(-50);
  }
}

.search-wrap {
  position: fixed;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1000;
  color: #fff;
  background-color: rgba($color-modal-background, 0.98);
  transform: translateY(-100%);
  transition: all 0.2s ease-out;

  &.search-open {
    top: 0;
    height: 100vh;
    opacity: 1;
    transform: translateY(0);
  }

  .icon {
    fill: currentColor;
  }

  .button-search-close {
    @extend .button-modal-close;

    &:hover {
      color: $color-accent;
    }
  }

  .search-form {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 100%;
    max-width: 586px;
    padding-bottom: rem-calc(10);
    transform: translate(-50%, -50%);
    border-bottom: 1px solid rgba(contrast($color-modal-background, 100%), 0.2);

    .search-input.form-input {
      box-shadow: none;
    }
  }

  .search-input {
    @extend h4;
    padding-right: 0;
    padding-left: 0;
    background: none;
    color: contrast($color-modal-background, 100%);

    &::placeholder {
      color: contrast($color-modal-background, 100%);
      text-transform: none;
      font-size: rem-calc(20);
    }

    &:focus {
      outline: none;
    }
  }

  .button-search-submit {
    top: rem-calc(25);
    color: contrast($color-modal-background, 100%);
  }

  .form-field {
    margin: 0;
  }
}

.button-search-submit {
  float: right;
  right: 0;
  background: none;

  &:hover,
  &:focus {
    color: $color-accent;
    outline: 0;
  }

  .icon {
    width: 25px;
    height: 25px;
  }
}

/*----------  Search Page  ----------*/

.search {
  .product-listing {
    padding-top: 0;
  }
}

.tabs {
  margin-top: rem-calc(80);
}

.content-results {
  margin-bottom: rem-calc(80);
}

.content-result {
  margin-bottom: rem-calc(40);

  h1 {
    @extend h4;
  }
}

.content-results-pagination {
  text-align: center;
}

/* scss-combine:components/navigation-mobile */
.nav-mobile-open {
  .site-canvas {
    transform: none;
    padding-top: $topbar-height;
  }

  .top-bar {
    position: fixed;
    z-index: $nav-mobile-z;
    top: 0;
    left: 0;
    width: 100%;
  }

  .button-mobile-nav-toggle {
    .burger {
      transform: rotate(-45deg);
      &:before { transform: rotate(90deg); }
      &:after { display: none; }
    }
  }
}

.button-mobile-nav-toggle {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);

  &.has-promo {
    top: auto;
    bottom: 15px;
    transform: translateY(0);
  }
}

.button-mobile-nav-back {
  position: relative;
  bottom: 15px;
  left: -5px;
  display: none;
  color: contrast($color-topbar-background, 100%);
  background: transparent;

  .icon {
    width: 9px;
    height: 14px;
  }

  .mobile-nav-open & {
    display: block;
  }
}

.button-mobile-nav-toggle {
  left: 10px;
  height: 28px;

  @include media($min: $breakpoint-s) { display: none; }

  &:focus { outline: none; }

  .burger {
    display: block;
    width: 16px;
    height: 2px;
    background: contrast($color-topbar-background, 100%);
    border-radius: 1px;
    transition: transform 0.2s ease;

    &:before,
    &:after {
      display: block;
      width: 16px;
      height: 2px;
      background: contrast($color-topbar-background, 100%);
      border-radius: 1px;
      content: '';
      transition: transform 0.2s ease;
    }

    &:before {
      transform: rotate(0deg) translate3d(0, -5px, 0);
    }

    &:after {
      transform: translateY(3px);
    }
  }
}

.button-mobile-nav-cart {
  position: absolute;
  top: 50%;
  right: 15px;
  height: 28px;
  transform: translateY(-38%);

  &.has-promo {
    top: auto;
    bottom: 8%;
    transform: translateY(0);
  }

  @include media($min: $breakpoint-s) { display: none; }

  a {
    display: block;
    height: 100%;
    vertical-align: middle;
    text-align: center;

    svg {
      fill: contrast($color-topbar-background, 100%);
    }
  }
}

.navigation-mobile {
  position: fixed;
  z-index: $nav-mobile-z;
  top: $topbar-height;
  width: 100%;
  height: calc(100vh - 56px);
  display: none;
  overflow-y: auto;
  background-color: rgba($color-topbar-background, 0.97);
  border-top: 1px solid rgba(#fff, 0.1);
  -webkit-overflow-scrolling: touch;

  @include media($min: $breakpoint-s) {
    display: none !important;
  }

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: translateX(-100%);
    transition: transform 0.3s ease;
  }

  &.visible {
    transform: translateX(0);
    opacity: 1;
  }

  .search-form {
    position: relative;
    display: block;
    padding: 10px;
    max-width: none;
  }

  .search-input {
    @extend .form-input;
    width: 100%;
  }

  .button-search-submit {
    position: absolute;
    right: 27px;
    top: 38px;
    padding: 0;
    background: none;

    .icon-search {
      width: 16px;
      height: 16px;
    }
  }

  a { padding: 9px 0; }

  .navigation {
    padding: 20px;
    float: none;
    border-bottom: 1px solid rgba($color-topbar-text, 0.1);
    transition: transform 0.2s ease;

    li,
    a { display: block; }

    .parent > a { color: contrast($color-modal-background, 60%); }
  }

  .nav-mobile-item,
  .nav-mobile-item a,
  .button-util,
  .currency-selector .dropdown-toggle,
  .children a {
    font-family: $font-secondary;
    font-size: rem-calc(16);
    color: $color-topbar-text;
    text-transform: uppercase;
  }

  .nav-mobile-panel-child {
    .nav-mobile-item a {
      @extend %body-text-treatment;
      color: contrast($color-modal-background, 60%);
      text-transform: none;
    }
  }

  .utils {
    padding: 20px;

    a { display: block; }

    .mobile-account-utils {
      display: inline-block;
    }
  }

  .icon-account,
  .icon-wishlist,
  .icon-cart {
    width: 18px;
    margin-right: 11px;
  }

  .dropdown-panel {
    position: static;
    padding-top: 0;
  }

  .currency-selector {
    margin-left: 0 !important; // override all styles for mobile

    .dropdown-toggle.top-bar-button {
      height: auto;
      padding-left: 0;
    }

    .dropdown-panel {
      background-color: transparent;
      padding-top: 0;
    }
  }

  .icon-arrow-down {
    margin-top: -0.05rem;
    transform: rotate(270deg);
  }
}

// Mobile nav styles
.nav-mobile-container {
  position: relative;
  overflow: hidden;
}

.nav-mobile-panel {
  width: 100%;
  transition: transform 0.5s ease;

  &.is-active {
    position: relative;
  }

  &.is-right,
  &.is-left {
    position: absolute;
    top: 0;
  }

  &.is-right {
    transform: translateX(100%);
  }

  &.is-left {
    transform: translateX(-100%);
  }

  &.force-state {
    transition: none;
  }

  .nav-mobile-panel-title {
    margin-bottom: 0.5rem;
  }
}

/* scss-combine:components/products/product-listing */
.product-listing {
  &.section {
    @include media($max: $breakpoint-s) {
      padding-top: 0;
    }
  }
}

/*----------  Category Banner  ----------*/

.category-banner {
  .container {
    position: relative;

    @include media($max: $breakpoint-s) {
      background-color: $color-background-secondary;
    }
  }
}

.category-banner-image {
  @include maintain-ratio(2 1);
  background-repeat: no-repeat;
  background-size: cover;

  .banner-ratio-smallest & { @include maintain-ratio(8 1); }
  .banner-ratio-small & { @include maintain-ratio(6 1); }
  .banner-ratio-medium & { @include maintain-ratio(4 1); }
}

.category-banner-description {
  padding: rem-calc(37 0);
  text-align: center;

  @include media($min: $breakpoint-s) {
    position: absolute;
    left: $gutter;
    bottom: 0;
    width: rem-calc(380);
    padding: rem-calc(25 31 32);
    background-color: rgba($color-carousel-background, 0.8);
    color: contrast($color-carousel-background, 100%);
    text-align: left;
  }
}


/*----------  Listing Header  ----------*/

.listing-header {
  @extend %clearfix;
  margin-bottom: $double-gutter;

  @include media($max: $breakpoint-s) {
    margin-left: -$gutter;
    margin-right: -$gutter;
    text-align: center;
    background-color: $color-background-secondary;
  }

  .listing-utils {
    @include media($max: $breakpoint-s) {
      display: none;
    }
  }
}

.listing-title {
  @extend h3;

  @include media($min: $breakpoint-s) {
    float: left;
  }
}

.listing-description {
  clear: left;
}

.listing-utils {
  float: right;

  .sorting,
  .button-group,
  .form-label,
  .form-select-wrapper {
    display: inline-block;
    vertical-align: middle;
    margin-left: 16px
  }

  .form-field {
    margin-bottom: 0;
    margin-right: 10px;
  }

  .button-group {
    @include media($max: $breakpoint-s) {
      display: none;
    }
  }
}

/*----------  Listing Grid  ----------*/

.product-listing-grid {
  @include media($min: $breakpoint-s) {
    @include span(9);
    @include last;
  }

  .no-sidebar & {
    @include media($min: $breakpoint-s) {
      @include span(12);
      @include last;
    }
  }

  .listing-view {
    .js & {
      display: none;
    }

    &.active {
      display: block;
    }
  }

  .product-grid-item {
    margin-bottom: rem-calc(60);

    @include media($min: $breakpoint-xs) {
      @include gallery(6 of 12);
    }

    @include media($min: $breakpoint-m) {
      @include gallery(4 of 12);
    }

    .no-sidebar & {
      @include media($min: $breakpoint-xs) {
        @include gallery(6 of 12);
      }

      @include media($min: $breakpoint-s) {
        @include gallery(4 of 12);
      }

      @include media($min: $breakpoint-m) {
        @include gallery(3 of 12);
      }
    }
  }

  .listing-wrapper {
    @extend %clearfix;
  }

  .pagination {
    @extend %center-x;
    margin: rem-calc(50px) 0 rem-calc(80px);
  }
}


/*----------  Sidebar  ----------*/

.listing-sidebar {
  @include media($min: $breakpoint-s) {
    @include span(3);
    padding-right: $double-gutter;
  }

  .listing-utils {
    float: none;
    padding: $double-gutter $gutter;
    margin: rem-calc(0 -15);
    border-top: 1px solid $color-border;

    @include media($min: $breakpoint-s) {
      display: none;
    }

    .sorting,
    .form-select-wrapper,
    .form-field {
      display: block;
      margin: 0;
    }

    .form-label {
      margin: 0 0 rem-calc(10);
    }

    .form-input {
      width: 100%;
    }
  }
}

.faceted-search-toggle {
  position: relative;
  display: block;
  margin: 1.5em 0;
  text-decoration: none;
  cursor: pointer;

  &::before {
    position: absolute;
    top: rem-calc(-15);
    right: rem-calc(-10);
    bottom: rem-calc(-15);
    left: rem-calc(-10);
    content: "";
  }

  @include media($min: $breakpoint-s) {
    display: none;

    &::before {
      display: none;
    }
  }

  &.is-open {
    .icon-plus {
      display: none;
    }
  }
}

.faceted-search {
  @include media($max: $breakpoint-s) {
    display: none;
  }

  &.visible {
    display: block;
  }
}

.facet-list {
  margin-bottom: 3em;
}

.facet-list-items {
  display: none;

  &.is-open,
  .is-open & {
    display: block;
  }

  .product-item-rating {
    > .icon-star {
      cursor: not-allowed;
    }

    .selected-rating {
      font-family: $font-secondary;
      font-size: rem-calc(15);
      letter-spacing: 0.09375em;
      text-transform: uppercase;
    }

    .icon-star {
      width: rem-calc(18);
      height: rem-calc(18);
    }
  }

  .product-item-rating-facet {
    &:hover {
      .icon-star {
        fill: $color-nav-active;
      }
    }

    &.full {
      .icon-star {
        fill: $color-nav-active;
      }
    }
  }
}

.facet-price {
  display: none;

  &.is-open {
    display: block;
  }
}

.faceted-search-option-columns {
  display: none;

  .is-open & {
    &.visible { display: block; }
  }
}

.facet-list-title {
  position: relative;
  font-size: rem-calc(16px);
  font-weight: normal;
  text-transform: none;
  cursor: pointer;

  &::before {
    position: absolute;
    top: rem-calc(-15);
    right: rem-calc(-10);
    bottom: rem-calc(-15);
    left: rem-calc(-10);
    content: "";
  }

  @include media($min: $breakpoint-s) {
    font-size: rem-calc(14px);

    &::before {
      display: none;
    }
  }
}

.facet-list-toggle {
  @include icon-circle($size: 20px);
  position: relative;
  float: right;
  color: $color-meta;
  cursor: pointer;

  &::before,
  &::after {
    position: absolute;
    display: block;
    background: $color-meta;
    content: "";
  }

  &::before {
    top: rem-calc(3);
    left: rem-calc(7);
    width: rem-calc(2);
    height: rem-calc(10);
    transition: all 0.1s ease;

    .is-open & { display: none; }
  }

  &::after {
    top: rem-calc(7);
    left: rem-calc(3);
    width: rem-calc(10);
    height: rem-calc(2);
  }
}

.facet-item,
.facet-toggle-more {
  margin-bottom: rem-calc(10);
  font-family: $font-secondary;
  font-size: rem-calc(13px);
  letter-spacing: 0.0625em;
  color: $color-meta;
  text-transform: uppercase;

  a {
    color: inherit;
  }

  .icon {
    width: 9px;
    height: 6px;
  }

  &.toggle-link-text-on {
    display: none;
  }
}

.facet-toggle-more {
  display: none;

  .is-open & {
    display: block;
  }
}

// Facet option list expand
.facet-default.is-open {
  display: block;
}

.selected-facet {
  color: $color-accent;
  font-weight: bold;

  a {
    &:after {
      @include icon-close($color-accent);
      display: inline-block;
      width: rem-calc(10);
      height: rem-calc(10);
      margin-left: rem-calc(5);
      content: "";
    }
  }
}

.facet-remove {
  font-size: rem-calc(13px);
}

.facet-price {

  @include media($min: $breakpoint-m) {
    @include layout-group;
    display: none;

    &.is-open {
      display: block;
    }
  }

  .form-actions {
    @include media($min: $breakpoint-xs) {
      @include layout-group-item;
    }

    @include media($min: $breakpoint-s) {
      display: block;
      margin-top: 6%;
    }

    @include media($min: $breakpoint-m) {
      @include layout-group-item;
      margin-top: 0;
      vertical-align: top;
    }
  }

  .button {
    padding-right: 1em;
    padding-left: 1em;

    @include media($min: $breakpoint-xs, $max: $breakpoint-m) {
      width: 94%;
    }
  }

  .form-field {
    @include layout-group-item;
    padding-right: 6%;

    .form-input {
      padding-left: 8%;
      padding-right: 8%;
      text-align: center;
      -moz-appearance: textfield;

      &::-webkit-inner-spin-button,
      &::-webkit-outer-spin-button {
        -webkit-appearance: none;
        margin: 0;
      }
    }
  }

  .form-input {
    width: 100%;
  }

  .icon-loop {
    position: relative;
    top: 1px;
    left: 1px;
    width: rem-calc(16);
    height: rem-calc(16);
  }
}

/* scss-combine:components/products/product-listing-item */
.products-featured,
.products-related {
  .container {
    @include media($min: $breakpoint-s) {
      padding-left: 50px;
      padding-right: 50px;
      max-width: $max-width + 100;
    }
  }

  .product-grid-item {
    margin-bottom: 3em;

    @include media($min: $breakpoint-xs) {
      margin-bottom: 0;
    }
  }

  .product-item-thumbnail {
    margin-right: 10px;
  }

  .carousel-navigation-item {
    color: $color-meta;

    &.previous { left: -50px; }
    &.next { right: -50px; }
  }

  .tabs {
    @include media($max: $breakpoint-s) {
      display: flex;
    }
  }

  .tab-title {
    flex-basis: 50%;

    a {
