// Wishlists
.wishlist-form {
  position: relative;
  display: inline-block;
  padding: $double-gutter 0;

  @include media($max: $breakpoint-m) {
    width: 48%;
    text-align: right;
  }
}

.button-wide.add-to-wishlist {
  @include media($max: $breakpoint-m) {
    width: calc(100% - 5px);
    padding-right: 5px;
    padding-left: 5px;
  }
}

.add-to-wishlists {
  position: absolute;
  top: 85%;
  display: none;
  padding: 1.0625em 2.7em;
  text-align: left;
  background-color: $color-topbar-background;

  @include media($max: $breakpoint-m) {
    padding-right: 1.5em;
    padding-left: 1.5em;
  }

  a {
    color: $color-topbar-text;

    &:hover {
      color: $color-accent;
    }
  }

  &.animating,
  &.visible {
    display: block;
  }

  // initial state for enter transition
  &.animating-in {
    opacity: 0;
    transform: translateY(-15px);
    transition: transform 0.3s ease, opacity 0.3s ease;
  }

  // initial state for leave transition
  &.animating-out {
    opacity: 0;
    transition: opacity 0.2s ease;
  }

  // final states for both in and out
  &.visible {
    opacity: 1;
    transform: translateY(0);
  }

  &::before {
    position: absolute;
    top: -10px;
    left: 50%;
    width: 0;
    height: 0;
    border-top: 0;
    border-right: 10px solid transparent;
    border-bottom: 10px solid $color-topbar-background;
    border-left: 10px solid transparent;
    content: "";
    transform: translateX(-50%);
