/* scss-combine:components/products/product-options */

.product-options-container {
  margin: $double-gutter 0 0;

  .form-field {
    margin-bottom: 22px;

    &.#{$all-text-inputs} {
      width: 100%;
    }

    input[type="number"] { max-width: 25%; }

