/* scss-combine:components/products/product-single-tabs */
.product-tabs {
  @extend .container;
  border-bottom: 1px solid $color-border;

  .tabs {
    @include media($max: $breakpoint-s) {
      display: none;
    }
  }

  .accordion-title {
    position: relative;
    margin-right: -$gutter;
    margin-left: -$gutter;
    font-weight: bold;
    border-bottom: 2px solid $color-border;

    @include media($min: $breakpoint-s) {
      display: none;
    }

    &:first-of-type {
      border-top: 2px solid $color-border;
    }

    &.is-open {
      @extend .tab-title.active;

      .icon-minus {
        display: inline-block;
      }

      .icon-plus {
        display: none;
      }
    }

    a {
      display: block;
      padding: $gutter;
      color: inherit;
    }

    .icon {
      position: absolute;
      top: rem-calc(20);
      right: $gutter;
      width: rem-calc(16);
      height: rem-calc(16);
    }

    .icon-plus {
      display: inline-block;
    }

    .icon-minus {
      display: none;
    }
  }

  .tab-content-panel {
    @extend %clearfix;

    @include media($max: $breakpoint-s) {
      padding: $double-gutter $gutter;
      margin-right: -$gutter;
      margin-left: -$gutter;
      border-bottom: 2px solid $color-border;
    }
  }
}

//*----------  Reviews  ----------*/

.product-reviews-title {
  text-align: center;
}

.review-item {
  padding-top: $double-gutter;
