}

.review-item-title {
  @extend h5;
  margin-bottom: 0;
}

.review-item-body {
  margin: 0;
  font-weight: 300;
}

.reviews-more-wrap { text-align: center; }

.reviews-show-more-link {
  display: inline-block;
  font-size: rem-calc(16px);
  font-weight: bold;
  color: $color-accent;

  &:hover {
    .icon-arrow-down {
      transform: scale(2);
    }
  }

  .icon-arrow-down {
    display: block;
    width: 9px;
    height: 9px;
    margin: 11px auto 0;
    transition: transform 0.3s $ease-out-back;
  }
}

.num-reviews {
  &::before {
    content: attr(data-num-reviews);
  }

  &[data-num-reviews="()"] {
    display: none;
  }
}

//*----------  Product Details Block  ----------*/

.product-warranty {
  @include media($min: $breakpoint-s) {
    @include span(5.5);
  }

  & + .product-details-blocks {
    @include media($min: $breakpoint-s) {
      @include span(5.5);
      @include last;
    }
  }

  .product-details-block {
    width: 100%;
  }
}

.product-details-block {
  margin-bottom: 2em;
  font-family: $font-secondary;

  @include media($min: $breakpoint-xs) {
    @include gallery(4);
  }
}

.product-details-name {
  font-weight: bold;
  text-transform: uppercase;
}

.product-details-value {
  margin: 0.5em 0 0;
}


//*----------  Review Form  ----------*/

.review-form {
  @extend %form-base-style;
  border-radius: $border-radius;

  .form-input {
    width: 100%;
  }

  .rating-name,
  .rating-email,
  .rating-number {
    @include media($min: $breakpoint-s) {
      @include span(5.75);
    }
  }

  .rating-email {
    @include media($min: $breakpoint-s) {
      @include last;
    }
  }

  .rating-subject {
    @include break;
  }

  .rating-stars-label {
    @extend %placeholder;
    margin-right: 10px;
  }

  .icon-star-wrap {
    display: inline-block;
    transform: translateY(-1px);
  }

  .rating-stars-wrap {
    position: relative;
  }

  .product-item-rating {
    @extend .form-input;
    margin-bottom: 0;
    pointer-events: none;
    background-color: #fff;
  }

  .rating-stars {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;
  }

  .icon-arrow-down {
    position: absolute;
    top: 50%;
    right: 17px;
    width: 10px;
    height: 6px;
    transform: translateY(-50%);
  }

  #recaptcha_area {
    margin: 0 auto;
  }
}

.review-form-heading {
  margin-top: 0;
  text-align: center;
  text-transform: uppercase;
}

.form-submit-wrap {
  text-align: center;
}


// Layout
/* scss-combine:layout/layout */
.site-wrapper {
  width: 100%;
  overflow: hidden;
}

.mini-cart-open {
  .site-header,
  .site-canvas,
  .mini-cart {
    transform: translateX(-$mini-cart-width);
  }
}

.site-canvas {
  position: relative;
  padding-top: $topbar-height;
  transition: transform .3s ease, padding 0.2s linear;

  @include media($min: $breakpoint-s) {
    padding-top: $expanded-nav-height;
  }
}

.container {
  @include container($max-width);
  @extend %wrapper-padding;

  &.container-small {
    max-width: 820px;
  }
}

.section {
  padding: $section-padding 0;
  @extend %clearfix;

  &.error {
    text-align: center;
  }
}

/* scss-combine:layout/header */
.site-header {
  @include font-switcher($header-use-primary-font);
  position: fixed;
  top: 0;
  z-index: 900;
  width: 100%;
  background-color: $color-background;
  border-bottom: 1px solid $color-border;
  transition: transform 0.3s ease;

  @if (lightness($color-nav-background) < 30) {
    border-bottom-color: transparent;
  }

  button {
    background: none;
  }
}

// Top-bar
.top-bar {
  position: relative;
  z-index: 10;
  background-color: $color-topbar-background;
  color: $color-topbar-text;

  .icon {
    @extend %inline-middle;
    fill: currentColor;
  }

  .container {
    display: table;
    width: 100%;
    max-width: 1230px;
    min-height: $topbar-height;
    vertical-align: middle;

    @include media($max: $breakpoint-m) {
      max-height: $topbar-height;
      padding-left: 0;
      padding-right: 0;
    }

    // Add divider if colours are the same
    @if ($color-topbar-background == $color-nav-background) {
      &:after {
        @include media($min: $breakpoint-s) {
          display: block;
          border-bottom: 1px solid $color-border;
          content: "";
        }
      }
    }
  }
}

.top-bar-button {
  display: inline-block;
  vertical-align: top;
  height: $topbar-height;
  padding-top: rem-calc(20px);
  color: inherit;

  &:focus {
    outline: none;
    color: $color-accent;
  }

  &:hover {
    color: $color-accent;
  }
}

.top-bar-left,
.top-bar-center,
.top-bar-right {
  display: table-cell;
  vertical-align: middle;
}

.top-bar-left {
  white-space: nowrap;

  @include media($max: $breakpoint-m) {
    padding-left: 30px - $gutter;
  }

  @include media($max: $breakpoint-s) {
    display: none;
  }
}

.top-bar-center {
  width: 100%;

  .site-branding {
    max-height: $navbar-height;
    margin: 0;
    font-size: 24px;
    line-height: 1;
    color: $color-topbar-text;
    text-align: center;
    vertical-align: bottom;

    .site-branding-logo {
      max-height: calc(#{$navbar-height} - 1rem);
      padding: 20px 0;
      vertical-align: middle;
    }

    @include media($min: $breakpoint-s) {
      position: absolute;
      left: 50%;
      display: inline-block;
      margin-top: 0;
      text-align: left;
      transform: translate(-50%, -50%);
    }

    .logo-align-left &,
    .logo-align-right & {
      @include media($min: $breakpoint-m) {
        display: none;
      }
    }

    .text-logo & {
      margin-top: 0;
    }

    .long-logo-text & {
      font-size: 16px;
    }
  }
}

.top-bar-right {
  white-space: nowrap;

  .logo-align-center & { width: span(4); }

  > * {
    white-space: normal;
  }

  @include media($max: $breakpoint-m) {
    padding-right: 30px - $gutter;
  }
}

.customer-links,
.currency-selector {
  @extend %top-bar-font;
  display: inline-block;
  vertical-align: top;
}

.customer-links {
  position: relative;
  margin-right: rem-calc(20px);

  &:after {
    @extend %top-bar-sep;

    .no-currency-selector & { display: none; }
  }

  .icon-account {
    margin-right: 11px;
    width: rem-calc(15);
    height: rem-calc(15);
  }

  .button-text {
    vertical-align: top;
  }
}

.currency-selector {
  margin-left: 0;

  &.accounts-enabled {
    margin-left: rem-calc(6);

    .top-bar-button {
      padding-left: rem-calc(10px);
    }
  }

  .currency-selector-active {
    margin-right: 5px;
  }

  .top-bar-button {
    padding-right: rem-calc(10px);
  }

  .icon-arrow-down {
    margin: -0.3em 0 0 0.5em;
  }

  .dropdown-panel {
    background-color: $color-topbar-background;
    padding: 10px;
  }

  .dropdown-menu-item {
    white-space: nowrap;

    a {
      display: block;
      color: inherit;

      &:hover {
        color: $color-accent;
      }
    }
  }
}

.currencies {
  width: 10px;
  padding-left: 10px;
  overflow: hidden;
  background: transparent;
  border: 0;
  appearance: none;
}

.promo-message {
  @include font-switcher(#{inverse-bool($header-use-primary-font)});
  padding: rem-calc(16) rem-calc(32) rem-calc(16) rem-calc(16);
  overflow: hidden;
  font-size: rem-calc(13);
  color: $color-topbar-text;
  text-align: center;
  text-overflow: ellipsis;
  white-space: nowrap;
  vertical-align: middle;
  background-color: $color-topbar-background;

  @include media($max: $breakpoint-s) {
    white-space: normal;
  }

  a {
    color: $color-topbar-text;
    text-decoration: underline;

    &:hover {
      color: $color-accent;
      text-decoration: none;
    }
  }

  .top-bar-center & {
    position: absolute;
    left: 50%;
    display: inline-block;
    padding: 0;
    margin-top: 0;
    text-align: left;
    transform: translate(-50%, -50%);

    @include media($max: $breakpoint-m) {
      display: none;
    }
  }

  .site-alerts & {
    @include media($min: $breakpoint-m) {
      .logo-align-left &,
      .logo-align-right & {
        display: none;
      }
    }
  }
}

.customer-utils {
  text-align: right;
  @include media($max: $breakpoint-s) { display: none; }

  .button-wishlist {
    margin-right: rem-calc(38px);

    &:after {
      @extend %top-bar-sep;
    }
  }
}

.button-cart-toggle {
  padding-top: rem-calc(11);
  padding-left: 0;
  transition: color 0.3s ease;

  @include media($max: $breakpoint-s) {
    position: absolute;
    right: 10px;
    top: 50%;
    transform: translateY(-50%);
  }
}

.mini-cart-subtotal {
  @include font-switcher(#{inverse-bool($header-use-primary-font)});
  margin-right: rem-calc(6px);
  vertical-align: text-top;
  line-height: 1;

  @include media($max: $breakpoint-m) {
    display: none;
  }
}

/* Nav Bar */
.main-nav-bar {
  background-color: $color-nav-background;
  position: relative;

  @include media($max: $breakpoint-s) {
    display: none;
  }

  > .container {
    display: block;
    width: 100%;
    max-width: 1230px;
    height: $navbar-height;
    text-align: $header-logo-alignment;
    transition: height $transition-navbar;

    .compressed & {
      height: $topbar-height;
    }
  }

  .site-branding {
    display: inline-block;
    width: span(2);
    height: 100%;
    margin-top: 0;
    margin-bottom: 0;

    .logo-align-right & {
      float: right;
      margin: 0;
      padding-right: 6px;
      text-align: right;
    }

    .logo-align-left { float: left; }

    .logo-align-center & { display: none; }

    @include media($max: $breakpoint-m) {
      display: none;
    }

    &:before {
      @extend %ghost-center;
    }
  }

  .site-branding-link {
    display: inline-block;
    vertical-align: middle;
    width: 100%;
    height: calc(100% - 1rem);

    &:before {
      @extend %ghost-center;
    }

    .logo-align-right & {
      width: auto;
    }

    .compressed & {
      height: 80%;
    }
  }

  .site-branding-logo {
    max-height: 100%;
    vertical-align: middle;
    padding: 10px 0;
  }

  .site-branding-title {
    display: inline-block;
    font-size: 24px;
    line-height: 1.1;
    vertical-align: middle;
    overflow-wrap: break-word;
    word-break: break-all;
  }

  .navigation {
    display: inline-block;
    vertical-align: top;
    height: 100%;
    margin-left: gutter();
    text-align: center;
    width: span(8);

    .logo-align-right & {
      float: left;
      margin-left: 0;
      text-align: left;
      max-width: 75%;
      width: auto;

      @include media($max: $breakpoint-m) {
        max-width: none;
        width: 100%;
        float: none;
        text-align: left;
      }

      .logo-align-right & {
        .enforce-max-width { max-width: 95%; }
      }

      ul li:first-child .top-level-nav-link:first-child { padding-left: 0; }
    }

    .logo-align-center & {
      display: inline-block;
      vertical-align: middle;
      margin-left: 0;
      width: auto;
    }

    > .button-search-toggle {
      .logo-align-center & { display: none; }
    }

    .button-search-toggle {
      height: 100%;
      vertical-align: top;

      svg {
        vertical-align: middle;
        margin-top: -3px;
      }
    }

    @include media($max: $breakpoint-m) {
      width: span(11);
      height: 100%;
      margin-left: rem-calc(-12);
    }

    &:before {
      @extend %ghost-center;
    }

    > ul {
      display: inline-block;

      .logo-align-right & {
        max-width: 95%;
      }

      > li {
        display: inline-block;
        margin-right: rem-calc(24);

        &.dropdown {
          > a { padding: rem-calc(16 12); }
        }

        &:not(.dropdown),
        &:not(:last-child) {
          margin-right: rem-calc(24);
        }
      }

      li:nth-last-child(n+10),
      li:nth-last-child(n+10) ~ li {
        margin-right: 0;

        @include media($max: $breakpoint-l) {
          margin-right: rem-calc(10);
        }
      }
    }
  }

  .top-level-nav-link {
    padding: rem-calc(16 0);
    display: block;
    color: $color-nav;
    font-size: rem-calc(13);
    letter-spacing: 0.077em;
    text-transform: uppercase;
    transition: color 0.3s linear, background-color 0.3s linear, padding 0.2s linear, height 0.2s linear;

    @include media($min: 1230px) {
      padding: rem-calc(16 $main-nav-spacing);
    }

    &:hover:not(.pages) {
      color: $color-accent;
    }

    &.pages {
      span:hover {
        color: $color-accent;
      }
    }
  }

  .dropdown-open > a {
    color: contrast($color-nav-active, 100%);
    background-color: rgba($color-nav-active, 0.97);

    &:hover { color: contrast($color-nav-active, 100%); }
  }

  .dropdown-panel {
    text-align: left;
    background-color: rgba($color-nav-active, 0.97);
  }
}

.site-branding {
  font-weight: 300;
  font-size: 32px;
  color: $color-nav;
  font-family: inherit;

  a {
    color: inherit;
    text-transform: uppercase;
    text-decoration: none;
  }
}

.simple-list {
  .navigation {
    float: none;
    width: 100%;
    padding: 0 rem-calc(10);
    margin: 0;
    text-align: left;

    .top-level-nav-link {
      padding: rem-calc(5) 0;
      white-space: nowrap;
    }

    .parent {
      display: block;
      width: 100%;
    }

    ul ul {
      width: 100%;
      margin-left: rem-calc(10);
    }
  }

  .parent > a {
    font-family: $font-secondary;
    font-size: rem-calc(17);
    color: contrast($color-nav-active, 100%);

    @if $font-secondary == "Roboto Condensed" {
      font-weight: bold;
      text-transform: uppercase;
    }
  }
}

.nav-page {
  .dropdown-panel {
    min-width: calc(100% + #{$gutter});
    white-space: nowrap;
    line-height: 1.3;
    padding-left: $gutter;
    padding-bottom: $gutter;
    padding-right: $gutter * 2;

    .navigation-mobile & {
      white-space: normal;
    }

    li:not(:last-child) {
      margin-bottom: $gutter;

      .navigation-mobile & {
        margin-bottom: 0;
      }
    }

    ul {
      margin-top: rem-calc(10);
      margin-bottom: $gutter;
    }
  }
}

.mega-nav {
  position: static;

  &.dropdown-open > a {
    height: rem-calc(77);
    padding-bottom: 39px;

    .multi-row & {
      height: auto;
      padding-bottom: 1rem;
    }

    .compressed & {
      padding-bottom: 16px;
      height: rem-calc(55);
    }
  }
}

.mega-nav-panel {
  left: 0;
  width: 100vw;
  max-height: calc(100vh - #{$expanded-nav-height});
  padding: rem-calc(40px) rem-calc(15) 0 0;
  overflow-y: auto;
  text-align: center;
  transition: top $transition-navbar;

  .container { max-height: 100%; }

  .multi-row & { top: 53px; }

  .compressed & {
    top: rem-calc(55);
  }
}

.mega-nav-list {
  display: flex;
  justify-content: space-between;
  text-align: left;
  flex-direction: row;

  &.wrap-cols {
    flex-wrap: wrap;

    &:after {
      flex: auto;
      content: "";
    }
  }

  a {
    display: inline-block;
    margin-bottom: 0.75rem;
    color: contrast($color-nav-active, 100%);

    &:hover {
      text-decoration: underline;
    }
  }

  .parent {
    display: inline-block;
    padding: 0 rem-calc(15);
    margin-bottom: 1.5rem;
    text-align: left;
    vertical-align: top;
    white-space: nowrap;

    > a {
      font-family: $font-secondary;
      font-size: rem-calc(17);

      @if $font-secondary == "Roboto Condensed" {
        font-weight: bold;
        text-transform: uppercase;
      }
    }
  }

  .children {
    @if $font-primary == "Roboto Slab" {
      font-family: $font-primary;
    }

    a {
      opacity: 0.5;
    }
  }

  ul ul {
    margin-top: -0.75rem;
    margin-bottom: 0.75rem;
    margin-left: rem-calc(15);

    a {
      margin-bottom: 0;
      font-family: $font-secondary;
      font-weight: 300;
    }
  }

  ul ul ul {
    margin-top: 0;
  }

  // Tier styles for shop menu mega nav
  .tier-panel {
    padding: 0;
  }

  .tier-open > a { opacity: 1; }

  .tier-toggle {
    padding-top: 0 !important; //override padding from regular dropdown padding
  }
}

.search-button-wrap {
  float: right;
  height: 100%;
  text-align: right;

  @include media($max: $breakpoint-m) {
    width: span(1);
    color: $color-nav;
  }

  &:before {
    @extend %ghost-center;
  }

  .button-search-toggle {
    vertical-align: middle;

    &:hover {
      color: $color-accent;
    }

    &:focus {
      outline: none;
      color: $color-accent;
    }
  }
}

// Breadcrumbs
.breadcrumbs {
  margin-top: rem-calc(40px);
  margin-bottom: rem-calc(25px);
  text-align: center;

  .page-header &,
  .product-listing & { margin-top: 0; }
}

.breadcrumb {
  display: inline-block;
  font-size: rem-calc(13);
  font-weight: normal;
  letter-spacing: 0.077em;
  text-transform: uppercase;

  @if ($font-secondary == "Roboto Condensed") {
    font-family: $font-secondary, sans-serif;
  }

  &:not(:last-child) {
    &::after {
      content: "/";
    }
  }

  a:hover {
    text-decoration: underline;
  }
}

.breadcrumb-label {
  color: $color-bold;

  &:not(.breadcrumb-link) {
    color: $color-meta;
  }
}

.product-listing-desktop-breadcrumbs {
  display: none;

  @include media($min: $breakpoint-s) {
    display: block;
  }
}

.product-listing-mobile-breadcrumbs {
  display: block;

  @include media($min: $breakpoint-s) {
    display: none;
  }
}

/* scss-combine:layout/footer */
$color-footer-background-contrast: contrast($color-footer-background, 90%);

.site-footer {
  background-color: $color-footer-background;
  color: rgba($color-footer-text, 0.5);
  font-weight: 300;
  font-size: 14px;
  border-top: 1px solid rgba($color-footer-background-contrast, 0.1);

  a {
    color: inherit;

    &:hover {
      text-decoration: underline;
    }
  }

  .footer-title {
    @extend h6;
    color: $color-footer-text;
    margin-top: 40px;
  }

  .container {
    padding-top: 35px;
  }

  .footer-col {
    &:last-child {
    }
  }

  .footer-nav {
    @include media($min: $breakpoint-s) {
      @include span(3);
    }

    @include media($min: $breakpoint-m) {
      @include span(2);
    }

    li {
      margin-bottom: rem-calc(8);
    }
  }

  .navigation {
    ul ul {
      margin-left: 1em;

      li {
        line-height: 1.75;
      }
    }
  }

  .footer-info {
    @include span(5);
    padding-right: 5%;
  }

  .footer-social {
    @include media($max: $breakpoint-m) {
      clear: both;
      padding-top: 3em;
    }

    @include media($min: $breakpoint-m) {
      @include span(3);
      @include last;
    }

    a {
      color: inherit;

      &:hover {
        text-decoration: none;
        color: rgba($color-footer-text, 1);
      }
    }
  }

  .social-media-link {
    display: inline-block;
    margin-right: rem-calc(20);
  }

  .bottom-bar {
    @include span(12);
    width: 100%;
    border-top: 1px solid rgba($color-footer-background-contrast, 0.1);
    text-align: center;
  }
}

.footer-nav-wrap {
  @extend %clearfix;
  padding-bottom: 57px;
}

.button-top {
  position: fixed;
  right: 12px;
  bottom: 0;
  z-index: 1000;
  width: 45px;
  height: 45px;
  color: $color-background;
  opacity: 0;
  background-color: $color-accent;
  border-radius: 4px;
  transform: translateY(100%);
  transition: transform 0.2s ease-out, background-color 0.2s ease, color 0.2s ease;

  @include media($min: $breakpoint-s) {
    right: 38px;
  }

  &:hover {
    background-color: $color-body;
    color: contrast($color-nav-active, 100%);
  }

  &.show {
    opacity: 1;
    bottom: 12px;
    transform: translateY(0);

    @include media($min: $breakpoint-s) {
      bottom: 27px;
    }

    .mini-cart-open & {
      right: $mini-cart-width + 38px;
    }
  }

  .icon {
    width: 16px;
    height: 10px;
  }
}

.footer-payment-icons-list {
  margin-top: 30px;

  li {
    display: inline-block;
    width: 50px;
    margin: 42px 5px;

    .show-credits & {
      margin: 12px 5px 0;
    }
  }

  svg {
    max-width: 80%;
  }
}

.footer-site-copyright,
.footer-theme-credits {
  display: block;
  color: $color-footer-text;
  font-size: rem-calc(13);
  font-weight: 400;
  letter-spacing: 0.077em;
  text-transform: uppercase;

  @if $font-secondary == "Roboto Condensed" {
    font-family: $font-secondary;
  }
}

.footer-site-copyright {
  margin-top: 42px;
}

.footer-theme-credits {
  margin-bottom: 42px;
}


// Pages
/* scss-combine:pages/home */
/*----------  Home Products  ----------*/

.products-new {
  background-color: $color-background-secondary;

  .product-grid-item {
    margin-bottom: 2.5rem;

    @include media($min: $breakpoint-xs) {
      @include gallery(6 of 12);
      margin-bottom: 0;

      &:first-child,
      &:nth-child(2) {
        margin-bottom: 2.5rem;
      }
    }

    @include media($min: $breakpoint-s) {
      @include gallery(3 of 12);
      margin-bottom: 0;
    }
  }
}

.products-featured {
  padding-bottom: 0;
}

/*----------  Brand List  ----------*/


.brand-list {
  display: none;
  padding-bottom: 40px;
  background-color: $color-topbar-background;
  text-align: center;

  .brand-link {
    display: inline-block;
    vertical-align: middle;
    margin: 0 20px 39px;
    transition: opacity 0.3s ease;

    @include media($min: $breakpoint-s) {
      margin-left: 30px;
      margin-right: 30px;
    }

    &:hover {
      opacity: 0.8;
    }
  }

  .brand-logo {
    max-height: 40px;
  }
}


/*----------  Blog Recent Posts  ----------*/

.blog-latest-posts {
  background-color: $color-background-secondary;

  .post {
    margin-bottom: 3rem;

    @include media($min: $breakpoint-s) {
      @include gallery(4);
      margin-bottom: 0;
    }
  }

  .post-thumb {
    @extend %background-position-img;
    @include maintain-ratio($ratio: 380 286);

    img { @extend .show-for-sr; }
  }

  .post-title { margin-bottom: 0; }

  .post-read-more {
    @extend %link-text-treatment;
    display: inline-block;
    margin-top: 20px;
    color: $color-accent;
  }
}


/*----------  Newsletter  ----------*/

.default {
  .newsletter-signup {
    background-color: $color-background;
  }
}

/* scss-combine:pages/compare */
/*----------  Top-bar Tab  ----------*/

.compare-wrap {
  position: relative;
  display: none;
  height: $topbar-height;
  vertical-align: top;

  &.compare-active {
    display: inline-block;
  }

  &:hover {
    .compare-tab {
      opacity: 1;
      visibility: visible;
      transform: translate(-50%, 0);
    }
  }
}

.button-compare-toggle {
  @extend %top-bar-font;
  position: relative;
  height: $topbar-height;
  padding-top: rem-calc(6px);
  padding-right: rem-calc(47px);
  line-height: 0;
}

.compare-items-count {
  position: absolute;
  top: 17px;
  right: $gutter;
  display: inline-block;
  width: 24px;
  height: 24px;
  line-height: 24px;
  color: $color-topbar-background;
  text-align: center;
  background-color: $color-accent;
  border-radius: 50%;
}

.compare-tab {
  position: absolute;
  top: 100%;
  left: 3px;
  width: 410px;
  height: 90vh;
  padding: $double-gutter 0;
  overflow: scroll;
  text-align: center;
  background-color: $color-mini-cart-background;
  opacity: 0;
  visibility: hidden;
  transform: translate(-50%, -15px);
  transition: all 0.3s ease;

  @include media($min: $breakpoint-m) {
    left: 50%;
  }
}

.compare-items-list {
  margin-top: $gutter;
}

.compare-tab-item {
  @extend .mini-cart-item;
  display: none;

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: translateY(-10%);
    opacity: 0;
    transition: opacity 0.2s ease, transform 0.3s ease;
  }

  &.visible {
    transform: translateY(0);
    opacity: 1;
  }
}

.compare-tab-item-wrap {
  @extend .mini-cart-item-wrap;
}

.compare-tab-item-thumb {
  @extend .mini-cart-item-thumb;
}

.compare-tab-item-description {
  @extend .mini-cart-item-description;
  padding-right: $gutter;
  text-align: left;
}

.compare-tab-item-title {
  @extend .mini-cart-item-title;
}

.compare-tab-item-price {
  color: $color-accent;
  font-weight: 300;
  font-size: 20px;
}

.compare-tab-item-remove {
  position: absolute;
  top: $double-gutter;
  right: $double-gutter;
  line-height: 0;
  cursor: pointer;

  .icon-close {
    width: 10px;
    height: 10px;
    pointer-events: none; // allows JS event bubbling
  }
}

.compare-submit {
  margin-top: $gutter;

  &.is-disabled {
    @extend .button-disabled;
  }
}

/*----------  Compare Grid Item  ----------*/

.product-item-compare {
  .icon-check-mark {
    display: none;
    width: 12px;
    height: 8px;
    margin-right: 5px;
  }

  .input-checkbox:checked {
    & + .form-label-text {
      .icon-check-mark {
        display: inline-block;
      }
    }
  }

  .form-label-text {
    margin: 0;
  }
}


/*----------  Compare Page  ----------*/

.compare-content {
  margin-bottom: 200px;

  @include media($max: $breakpoint-s) {
    overflow-x: auto;
  }
}

.compare-table {
  text-align: center;
  border-color: transparent;

  .product-item-thumbnail {
    display: inline-block;
    width: 100%;
  }

  .product-item-thumbnail-image {
    @include maintain-ratio(1 1);
    background-repeat: no-repeat;
    background-position: 50% 50%;
    background-size: contain;

    .thumbnail-cover & {
      background-size: cover;
    }
  }
}

.compare-row {
  border-top-color: $color-border;

  &:first-child {
    border-color: transparent;
  }
}

.compare-table-heading {
  @extend %body-text-treatment;
  width: 20%;
  padding-left: 0;
  text-align: left;
  text-transform: none;
  font-weight: normal;
  vertical-align: top;
}

.compare-table-cell {
  min-width: 230px;
  vertical-align: top;
}

.compare-remove {
  display: block;
  margin-top: $gutter;
}

/* scss-combine:pages/cart */
.cart {
  .link {
    font-size: rem-calc(12px);
    cursor: pointer;
  }
}

.cart-table-header,
.cart-item {
  @include media($min: $breakpoint-s) {
    @include layout-group();
  }

  .cell {
    @include media($min: $breakpoint-s) {
      @include layout-group-item();
      padding-right: $gutter;
      vertical-align: middle;
    }

    &:last-child {
      padding-right: 0;

      @include media($min: $breakpoint-s) {
        text-align: right;
      }
    }
  }
}

.cart-empty { text-align: center; }


/*----------  Cart Table Header  ----------*/

.cart-table-header {
  padding-bottom: 25px;
  border-bottom: 1px solid $color-border;

  @include media($max: $breakpoint-s) {
    display: none;
  }

  .cell {
    @extend h5;
    color: $color-bold;
  }
}

.cart-item-options {
  @include media($min: $breakpoint-s) {
    width: 20%;
  }

  @include media($min: $breakpoint-m) {
    width: 30%;
  }
}

.cart-item-quantity,
.cart-item-price {
  @include media($min: $breakpoint-s) {
    width: 15%;
  }

  @include media($min: $breakpoint-m) {
    width: 10%;
  }
}


/*----------  Cart Table Items  ----------*/

.cart-item {
  padding: rem-calc(25px) 0;
  border-bottom: 1px solid $color-border;

  @include media($max: $breakpoint-s) {
    position: relative;
  }

  .cell {
    @include media($max: $breakpoint-s) {
      margin-bottom: 1em;
    }
  }
}

.cart-item-info {
  margin-bottom: $gutter;

  @include media($min: $breakpoint-xs) {
    margin-bottom: 24px;
  }

  @include media($min: $breakpoint-s) {
    width: 50%;
    margin-bottom: 0;
  }

  .product-brand {
    font-weight: bold;
  }

  .product-name {
    @extend h5;
    margin-bottom: 0.35em;
    display: block;
  }
}

.cart-item-info-wrap {
  @include layout-group;
}

.cart-item-image,
.cart-item-text {
  @include layout-group-item;
  vertical-align: middle;
}

.cart-item-image {
  width: 100px;
  border: 1px solid $color-border;

  img {
    display: block;
  }
}

.cart-item-text {
  padding-left: 20px;
  padding-right: 20px;

  @include media($min: $breakpoint-xs) {
    padding-left: $double-gutter;
  }
}

.cart-item-actions .link { margin-right: rem-calc(8px); }

.cart-label {
  margin-right: rem-calc(5px);
  color: $color-bold;

  .cart-item-quantity &,
  .cart-item-price & {
    @include media($min: $breakpoint-s) {
      display: none;
    }
  }

}

.quantity-control {
  display: inline-block;

  .button-control-action {
    @include icon-circle($size: 20px);
    line-height: 0;
    background-color: transparent;
    transition: color 0.3s ease, border-color 0.3s ease;

    &:hover {
      color: $color-accent;
      border-color: $color-accent;
    }

    &:focus {
      outline: none;
    }
  }

  .form-input[type="number"] {
    display: inline-block;
    width: rem-calc(35px);
    padding: 0;
    font-family: $font-primary;
    color: $color-body;
    text-align: center;
    cursor: pointer;
    box-shadow: none;
    -moz-appearance:textfield;

    &::-webkit-inner-spin-button,
    &::-webkit-outer-spin-button {
      appearance: none;
    }

    &:focus {
      border-bottom: 1px solid $color-meta;
    }
  }

  .icon {
    width: 10px;
    height: 10px;
    line-height: 0;
    pointer-events: none;
  }
}

.cart-item-price {
  @include media($min: $breakpoint-s) {
    position: relative;
  }

  .price-discounted { text-decoration: line-through; }
}

.cart-item-update {
  display: block;
  visibility: hidden;
  opacity: 0;
  transition: opacity 0.3s ease;

  .needs-updating & {
    visibility: visible;
    opacity: 1;
  }

  @include media($min: $breakpoint-s) {
    position: absolute;
    right: 0;
  }
}

.cart-item-remove {
  position: absolute;
  top: rem-calc(25px);
  right: 0;

  @include media($min: $breakpoint-s) {
    top: 0;
  }

  @include media($min: $breakpoint-l) {
    top: 50%;
    right: rem-calc(-53px);
    transform: translateY(-50%);
  }

  .icon {
    width: 12px;
    height: 12px;
  }
}

/*----------  Gift Wrapping  ----------*/

.giftwrapping-detail {
  color: $color-meta;
  font-family: $font-secondary;
  font-size: rem-calc(14px);
}

.giftwrap-modal {
  max-width: rem-calc(789px);
  padding: 5vw;
  background-color: $color-background;
  border-radius: $border-radius;

  .form-field-title {
    margin-left: 0;
    display: block;
  }

  .form-radio + .form-label-text {
    font-weight: 300;
  }

  .form-input:not(.form-radio) {
    width: 100%;
  }
}

.giftwrap-subtitle,
.giftwrap-title {
  text-align: center;
}

.giftwrap-title {
  margin-top: 0;
}

.giftwrap-subtitle {
  @extend h6;
  margin-bottom: 1.5em;
  color: $color-meta;
}

.giftwrap-form {
  .form-actions {
    text-align: center;
  }

  .button + .button {
    margin-left: 1.5em;
  }
}

/*----------  Cart Totals  ----------*/

.cart-totals {
  @extend %clearfix;
}

.cart-totals-column {
  @include media($min: $breakpoint-s) {
    @include span(45%);
  }

  @include media($min: $breakpoint-m) {
    @include span(35%);
  }

  &:last-child {
    @include media($min: $breakpoint-s) {
      @include last;
    }
  }
}

.cart-totals-item {
  @extend %clearfix;
  padding: rem-calc(26px) 0;
  border-bottom: 1px solid $color-border;

  .link {
    float: right;
    clear: right;
  }

  .cart-total-item {
    + .link {
      span { display: none; }

      &:before {
        content: attr(data-change-text);
      }
    }
  }
}

.cart-total-label {
  font-weight: normal;
}

.cart-total-item {
  float: right;

  &.grand-total {
    font-weight: bold;
    color: $color-bold;
  }
}

.list-bulleted .link {
  display: block;
}

.cart-actions {
  @include media($max: $breakpoint-xs) {
    .button {
      display: block;
      margin: 2rem 0;
    }
  }

  @include media($min: $breakpoint-xs) {
    @include layout-group($gutter: 2rem);
    margin: 2rem 0 0 -2rem;

    .button {
      @include layout-group-item;
      width: 50%;
      padding-right: 0;
      padding-left: 0;
    }
  }
}

.cart-additional-checkout-buttons {
  @extend %clearfix;
  text-align: center;

  // scss-lint:disable ImportantRule, QualifyingElement
  p { float: none !important; }

  form { clear: both; }

  > * {
    margin-bottom: rem-calc(15);
  }

  &:last-child {
    margin-bottom: 0;
  }
}

.apple-pay-checkout-button {
  display: none;
  box-sizing: border-box;
  min-width: rem-calc(176);
  height: rem-calc(45);
  background: {
    size: 100% 60%;
    repeat: no-repeat;
    position: 50% 55%;
  }
  border-radius: 5px;

  @if lightness($color-background) < 40 {
    background-image: -webkit-named-image(apple-pay-logo-black);
    background-color: white;
  } @else {
    background-image: -webkit-named-image(apple-pay-logo-white);
    background-color: black;
  }

  .apple-pay-supported & {
    display: inline-block;
  }
}

// Coupons

.coupon-code {
  border: 0;
}

.form-coupon {
  position: relative;
}

.coupons-active {
  margin-bottom: 0;
}

// Shipping

.shipping-calculator {
  @extend %clearfix;
  display: none;
  margin-top: 1.75rem;

  &.visible {
    display: block;
  }

  .shipping-zip {
    float: left;
  }

  .get-shipping-quotes {
    float: right;
  }

  .form-select-wrapper {
    [type="text"] + :after {
      display: none;
    }
  }

  [name="shipping-state"] {
    width: 100%;
  }
}

.shipping-quotes {
  clear: both;
}

.shipping-quote-item {
  cursor: pointer;
  margin-left: 2px;

  &:hover {
    color: $color-accent;
  }

  .form-label-text {
    margin-bottom: 0;

    .ups-shipping-badge {
      vertical-align: middle;
      margin-top: -2px;
      margin-left: 3px;
      margin-right: 3px;
    }
  }
}

// Gift Certificate

.form-gift-certificate {
  display: none;
  margin-top: 1.75rem;

  &.visible {
    display: block;
  }
}

/* scss-combine:pages/account */
.account-wrapper {
  @include container;
  @extend %wrapper-padding;
  max-width: $max-width;
  padding-top: $section-padding;
  padding-bottom: $section-padding;

  .form-field {
    @extend .form-field;
  }

  .form-field-email .form-field-control,
  .form-field-password .form-field-control {
    position: relative;
    display: block;

    &:after {
      position: absolute;
      right: rem-calc(17px);
      bottom: rem-calc(20px);
      display: block;
      content: "";
    }
  }

  .form-field-email .form-field-control {
    &:after {
      @include icon-email($color-meta);
      width: 16px;
      height: 12px;
    }
  }

  .form-field-password .form-field-control {
    &:after {
      @include icon-password($color-meta);
      width: 12px;
      height: 16px;
    }
  }

  .form-select-wrapper {
    position: relative;
    display: block;
    height: rem-calc(55);
    padding: rem-calc(19 17 18);
    color: $color-input;
    border: 0;
    border-radius: $border-radius;
    transition: border-color 0.2s $ease-out-quad;

    select {
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 100%;
      cursor: pointer;
      appearance: none;
    }
  }
}

.account-login {
  .account-button-secondary {
    margin-left: rem-calc(20);
  }
}

/*----------  Auth Modal  ----------*/

.login-modal {
  .modal-title {
    margin: 0;
    color: contrast($color-modal-background, 100%);
    text-align: center;
  }

  .account-sub-heading {
    @extend h3;
    text-align: center;
  }

  .account-login-wrapper {
    display: block;
    padding: 0;

    @include media($min: $breakpoint-s) {
      display: flex;
      align-items: stretch;
      justify-content: space-between;
    }
  }

  .account-login,
  .account-create {
    display: block;
    border-radius: $border-radius;
  }

  .account-login {
    background-color: $color-background;

    @include media($max: $breakpoint-s) {
      margin-bottom: $double-gutter;
    }
  }

  .account-button-secondary {
    padding: 0 0 3px;
    background-color: transparent;
    color: $color-button-secondary;
    border-radius: 0;
    border-bottom: 1px solid;
  }

  .account-create {
    color: contrast($color-modal-background, 100%);
    border: 2px solid rgba($color-background, 0.10);
    background: transparent;

    .account-sub-heading { color: contrast($color-modal-background, 100%); }

    .button {
      @extend .button-secondary;
      @extend %center-x;
      color: contrast($color-modal-background, 100%);
      border-color: contrast($color-modal-background, 100%);
    }
  }

  .account-create-facts li:before {
    @include icon-check-mark(contrast($color-modal-background, 100%));
  }

  .account-page-forgot .account-body {
    position: relative;
    max-width: $modal-width-narrow;
    margin: 0 auto;
    padding: 5vw;
    background-color: $color-background;
    border-radius: $border-radius;

    .account-sub-heading { display: none; }
  }
}

// Modal revealer panels
.login-register-block,
.forgot-password-block {
  display: none;

  &.animating,
  &.visible {
    display: block;
  }

  // initial state for enter transition
  &.animating-in {
    opacity: 0;
    transform: translateX(-100%);
    transition: transform 0.3s ease, opacity 0.3s ease;
  }

  // initial state for leave transition
  &.animating-out {
    opacity: 0;
    transform: translateX(100%);
    transition: transform 0.3s ease, opacity 0.3s ease;
  }

  // final states for both in and out
  &.visible {
    opacity: 1;
    transform: translateX(0);
  }
}

/*----------  Login  ----------*/

.account-login-wrapper { max-width: none; }

.account-login,
.account-create {
  width: 100%;
  padding: $double-gutter;

  @include media($min: $breakpoint-s) {
    width: span(5.75);
    padding: 2vw;
  }

  @include media($min: $breakpoint-m) {
    padding: 5vw;
  }
}

.auth-sub-heading {
  @extend h3;
  margin-top: 0;
  text-align: center;
}

.auth-login-form {
  .button,
  .auth-button-alt {
    display: inline-block;
    width: span(5.75);
  }

  .auth-button-alt {
    text-align: right;
    text-decoration: underline;
  }
}

.account-create-facts {
  padding-left: 0;
  font-family: $font-secondary;
  font-size: 13px;
  letter-spacing: 1px;
  text-transform: uppercase;
  list-style: none;

  li {
    margin-bottom: 0.5em;

    &:before {
      @include icon-check-mark($color-body);
      display: inline-block;
      width: 13px;
      height: 13px;
      margin-right: rem-calc(15px);
      content: "";
    }
  }
}

/*----------  Forgot Password  ----------*/

.auth-forgot-password-message { text-align: center; }

.auth-forgot-password {
  .form { @include layout-group; }

  .form-field,
  .form-actions {
    @include layout-group-item;
    vertical-align: bottom;
  }

  .form-actions {
    width: 33%;
    text-align: right;
  }
}

/*----------  Nav  ----------*/

.account-nav-list {
  text-transform: uppercase;
  border: 0;

  @if $font-primary == "Roboto Slab" {
    font-family: $font-secondary;
  }
}

.account-nav-item {
  color: inherit;
  border: 0;

  &.is-active {
    border: 0;
    color: $color-accent;
  }
}

.account-nav-link {
  padding: rem-calc(10) 0;
}

/*----------  Products  ----------*/

.account-product-item {
  @extend .product-grid-item;
  margin-bottom: 3rem;

  @include media($min: $breakpoint-xs) {
    @include gallery(6 of 12);
  }

  @include media($min: $breakpoint-m) {
    @include gallery(4 of 12);
  }
}

/* scss-combine:pages/blog */
  /*----------  Blog Index  ----------*/

.blog {
  .post-thumb {
    background: none !important; // over-ride inline style
  }

  .post-read-more {
    @extend .button;
    @extend .button-wide;
  }
}

.blog-items-list {
  .post {
    max-width: $blog-index-width;
    padding-bottom: $section-padding;
    margin: $section-padding auto 0;
    border-bottom: 1px solid $color-border;

    &:last-child {
      border: 0;
    }
  }

  .post-thumb {
    margin-bottom: 1rem;
  }

  .post-title,
  .post-meta {
    text-align: center;
  }

  .post-title {
    @extend h1;
    margin-top: 0;
    margin-bottom: 1rem;
  }

  .post-summary {
    margin-bottom: $double-gutter;
    text-align: center;
  }

  .post-summary-actions {
    text-align: center;
  }

  .share-buttons-nav {
    display: inline-block;
    margin-top: rem-calc(30px);

    @include media($min: $breakpoint-xs) {
      margin-top: 0;
      margin-left: rem-calc(30px);
    }
  }
}

.post-thumb,
.featured-image-container {
  display: block;

  img {
    width: 100%;
  }
}

.post {
  .icon-calendar,
  .icon-avatar {
    width: 16px;
    height: 16px;
    margin-right: 10px;
    transform: translateY(-1px);
  }
}

.post-meta {
  font-weight: 400;
  font-size: rem-calc(11);
  letter-spacing: 0.09em;
  color: $color-meta;
  text-transform: uppercase;

  @if $font-secondary == "Roboto Condensed" {
    font-family: $font-secondary;
  }

  > * { vertical-align: middle; }
}

.post-date {
  margin-right: 38px;
}


/*----------  Blog Single  ----------*/

.blog-content {
  padding: 0 20px;

  @include media($min: $breakpoint-s) {
    padding: 0;
  }
}

.blog-post-single {
  .post-header {
    background-color: transparent;
  }

  .post-meta {
    margin-bottom: 0;
  }

  .post-main {
    max-width: $blog-index-width;
    margin: 0 auto;
  }

  .post-footer {
    margin-bottom: rem-calc(160px);
    text-align: center;

    .post-tags,
    .share-buttons-nav {
      margin-top: $section-padding;
    }
  }
}

.post-tag {
  padding: rem-calc(10px);
  margin-right: 6px;
  background-color: $color-background-secondary;
  border-radius: $border-radius;
  transition: all 0.3s ease;

  &:before { content: "#"; }

  &:hover {
    color: contrast($color-body, 80%);
    background-color: shade($color-background-secondary, 20%);
  }
}

/*----------  Share Buttons  ----------*/

.share-buttons { line-height: 0; }

.social-link {
  display: inline-block;
  margin-right: rem-calc(10);
  vertical-align: middle;

  a {
    color: $color-meta;

    &:hover {
      color: $color-accent;
    }
  }

  .icon {
    width: 28px;
    height: 14px;
  }
}

.fb-like iframe { max-width: none; }

/* scss-combine:pages/brands */
.brand-items-list {
  font-size: 0;
}

.brand-item {
  display: inline-block;
  width: 50%;
  padding: $gutter;
  font-size: $font-size;
  text-align: center;
  vertical-align: bottom;

  @include media($min: $breakpoint-s) {
    width: span(25%);
  }

  a {
    position: relative;
    display: block;
    padding: 50% 0;
    color: inherit;
    border: 1px solid $color-border;
    transition: all 0.6s ease-out;

    &:hover {
      background-color: $color-bold;
      color: contrast($color-bold, 100%);
      transition: all 0.3s ease-out
    }

    .brand-name {
      position: absolute;
      max-width: 75%;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
    }
  }
}

.brand-item-title {
  font-family: $font-secondary;
  text-transform: uppercase;
  margin: 0;
}

/* scss-combine:pages/contact-us */
.form-contact-us {
  @extend %form-base-style;

  .form-input { width: 100%; }

  .form-actions { text-align: center; }
}

/* scss-combine:pages/gift-certificates */
.giftcard-sidebar {
  @include media($min: $breakpoint-s) {
    @include span(3);
    padding-right: $double-gutter;
  }
}

.giftcard-content {
  padding-bottom: $section-padding;

  @include media($min: $breakpoint-s) {
    @include span(9);
    @include last;
  }

  .giftcard-subheading {
    margin: 1.5em 0;
  }
}

.giftcard-nav {
  margin-bottom: 3rem;
  text-align: left;
}

.giftcard-header {
  @extend h2;
  padding-bottom: rem-calc(10);
  border-bottom: 1px solid $color-border;
}

.giftcard-nav-item {
  @extend %link-text-treatment;
  margin: 0 rem-calc(8) rem-calc(10);
  text-decoration: none;
  color: $color-meta;

  &.is-active {
    color: $color-accent;
  }
}

.giftcard-body {
  .giftcertificates & {
    padding: rem-calc(50 72);
    background-color: $color-background-secondary;
  }

  .form-input:not(.form-radio) {
    width: 100%;
  }

  .giftcard-heading,
  .giftcard-subheading {
    display: none;
  }
}

.giftcard-form-preview {
  iframe {
    width: 100%;
  }
}

.form-giftcard-balance {
  @include layout-group;

  .form-field,
  .form-actions {
    @include layout-group-item;
    vertical-align: bottom;
  }

  .form-field {
    width: 90%;
    padding-right: rem-calc(30);
  }
}

.giftcard-balance-container {
  margin: 0;
}

.giftcard-form-theme {
  .form-field-control {
    overflow: hidden;
  }

  .form-field {
    @extend %clearfix;
  }

  .form-label {
    @include gallery(4);
  }
}

.giftcard-form-actions {
  text-align: center;
}


#giftcard-amount {
  @include media($min: $breakpoint-s) {
    width: calc(50% - 8px);
  }
}

.giftcard-form-help-message {
  @include media($min: $breakpoint-s) {
    float: left;
    position: relative;
    top: -40px;
    margin-left: calc(50% + 10px);
  }
}

.giftcard-redeem-steps {
  margin-left: 1em;
}


/* scss-combine:pages/sitemap */
.sitemap-group {
  display: inline-block;
  width: 33%;
  margin-bottom: 50px;
  padding: 0 15px;
  vertical-align: top;

  @include media($max: $breakpoint-m) {
    width: 32%;

    &:last-child {
      margin-bottom: 0;
    }
  }

  @include media($max: $breakpoint-xs) {
    width: 100%;
  }
}

.sitemap-title {
  font-size: 16px;
}

.sitemap-showall {
  display: block;
  margin-top: 15px;
  font-weight: 600;
}
