components/common/multi-level-nav-item:
{{!
  @param all_item
         > either true or false, passed from parent template
}}

<a href="{{url}}" {{#if children}}data-toggle-mobile="{{url}}" data-mobile-name="{{name}}"{{/if}}>{{name}}{{#if children}}{{> svg/arrow-down}}{{/if}}</a>
{{#if children}}
  <ul class="nav-mobile-panel nav-mobile-panel-child is-right" data-mobile-menu="{{url}}">
    <li class="nav-mobile-item nav-mobile-panel-title">{{name}}</li>
    <li class="nav-mobile-item nav-mobile-item-all">
      <a href="{{url}}">{{#if all_item}}{{lang 'common.navigation_all'}}{{/if}} {{name}}</a>
    </li>
    {{#each children}}
      <li class="nav-mobile-item {{#if children}}has-children{{/if}}">
        {{> components/common/multi-level-nav-item}}
      </li>
    {{/each}}
  </ul>
{{/if}}

components/header/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-selector dropdown{{#if settings.account_creation_enabled}} accounts-enabled{{/if}}">
    <a class="dropdown-toggle top-bar-button" href="#">
      <span class="currency-selector-active button-text">{{currency_selector.active_currency_code}}</span>
      <span class="currency-selector-flag">{{{currency_selector.active_currency_flag}}}</span>
      {{> svg/arrow-down}}
    </a>
    <ul class="dropdown-panel" tabindex="-1">
      {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a href="{{switch_url}}">
              {{#if is_active}}
                <strong>{{name}}</strong>
              {{else}}
                {{name}}
              {{/if}}
            </a>
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}
