components/category/shop-by-brand:
{{#if shop_by_brand.length}}
  <div class="sidebar-block shop-by-brand">
    <h4 class="facet-list-title">{{lang 'category.shop_by_brand'}}</h4>
    <ul>
      {{#each shop_by_brand}}
        {{#if @index '<' 5}}
          <li class="facet-item">
            <a
              class="{{#if selected}}is-active{{/if}}"
              rel="nofollow"
              href="{{url}}"
              alt="{{name}}"
            >
              {{name}}
            </a>
          </li>
        {{/if}}
      {{/each}}
      {{#if shop_by_brand.length '>' 5}}
        <li class="facet-item">
          <a href="{{urls.brands}}">{{lang 'footer.view_all'}}</a>
        </li>
      {{/if}}
    </ul>
  </div>
{{/if}}

components/category/shop-by-price:
{{#if shop_by_price}}
  <div class="sidebar-block shop-by-price">
    <h4 class="facet-list-title">{{lang 'category.shop_by_price'}}</h4>
    <ul>
      {{#each shop_by_price}}
        <li class="facet-item">
          <a
            class="{{#if selected}}is-active{{/if}}"
            rel="nofollow"
            href="{{url}}"
            alt="{{low.formatted}} - {{high.formatted}}"
          >
            {{low.formatted}} - {{high.formatted}}
          </a>
        </li>
      {{/each}}
    </ul>
    {{#any shop_by_price selected=true}}
      <a href="{{category_url}}" class="link facet-remove">
        {{lang 'category.reset'}}
      </a>
    {{/any}}
  </div>
{{/if}}

components/category/sidebar:
{{#if category.subcategories}}
  <div class="category-nav facet-list">
    <h4 class="facet-list-title">{{lang 'category.sub_categories'}}</h4>
    <ul class="category-nav-list">
      {{#each category.subcategories}}
        <li class="facet-item">
          <a href="{{url}}">{{name}}</a>
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}

{{#if category.faceted_search_enabled}}
  <span class="link faceted-search-toggle" data-faceted-search-toggle>
    {{lang 'faceted_search.toggle'}}
    <span class="facet-list-toggle">
      <svg class="icon icon-plus"><use xlink:href="#icon-plus" /></svg>
      <svg class="icon icon-minus"><use xlink:href="#icon-minus" /></svg>
    </span>
  </span>
  <div class="faceted-search" data-faceted-search>
    {{#each category.facets}}
      {{#if visible}}
        {{#if items}}
          <div class="facet-list {{display}}" data-facet-filter>

            <h4
              class="facet-list-title {{#unless start_collapsed}}is-open{{/unless}}"
              data-facet-toggle
            >
              {{title}}
              <span class="facet-list-toggle"></span>
            </h4>

            {{{dynamicComponent 'components/faceted-search/facets'}}}

            {{#if remove_url}}
              <a
                class="link facet-remove"
                href="{{remove_url}}"
                data-faceted-search-facet
                data-facet-remove
              >
                {{lang 'faceted_search.clear'}}
              </a>
            {{/if}}
          </div>
        {{/if}}
        {{#compare facet 'price'}}
          <div class="facet-list {{display}}" data-facet-filter>

            <h4
              class="facet-list-title
              {{#unless start_collapsed}}is-open{{/unless}}"
              data-facet-toggle
            >
              {{title}}
              <span class="facet-list-toggle"></span>
            </h4>

            {{{dynamicComponent 'components/faceted-search/facets'}}}

            {{#if remove_url}}
              <a
                class="link facet-remove"
                href="{{remove_url}}"
                data-faceted-search-facet
                data-facet-remove
              >
                {{lang 'faceted_search.clear'}}
              </a>
            {{/if}}
          </div>
        {{/compare}}
      {{/if}}
    {{/each}}
  </div>
{{else}}
  {{> components/category/shop-by-price
    shop_by_price=category.shop_by_price
    category_url=category.url
  }}
  {{#if theme_settings.category-shop-by-brands}}
    {{> components/category/shop-by-brand category_url=category.url}}
  {{/if}}
{{/if}}
