components/common/navigation:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

<nav class="navigation">
  <ul>
    {{#each nav_items}}
      <li class="parent {{#if children}}tier-dropdown{{/if}}">
        <a class="top-level-nav-link {{#if children}}tier-toggle{{/if}}" href="{{url}}">{{name}}</a>
        {{#if children}}
          <ul class="tier-panel">
            <li class="parent-all"><a href="{{url}}">{{lang 'common.navigation_all'}} {{name}}</a></li>
            {{#each children}}
              <li {{#if children}}class="tier-dropdown"{{/if}}>
                <a {{#if children}}class="tier-toggle"{{/if}} href="{{url}}">{{name}}</a>
                {{#if children}}
                  <ul class="tier-panel">
                    <li class="parent-all"><a href="{{url}}">{{lang 'common.navigation_all'}} {{name}}</a></li>
                    {{#each children}}
                      {{> components/common/site-navigation-submenus}}
                    {{/each}}
                  </ul>
                {{/if}}
              </li>
            {{/each}}
          </ul>
        {{/if}}
      </li>
    {{/each}}
    {{#if settings.gift_certificates_enabled}}
      {{#if nav_items '==' pages}}
        <li><a href="{{urls.gift_certificate.purchase}}">{{lang 'header.gift_certificate_cta'}}</a></li>
      {{/if}}
    {{/if}}
  </ul>
</nav>

components/common/navigation-mega:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

{{#each nav_items}}
  <li class="parent {{#if children}}has-children tier-dropdown{{/if}}">
    <a href="{{url}}">{{name}}</a>
    {{#if children}}
      <ul class="children">
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul>
                {{#each children}}
                  {{> components/common/site-navigation-submenus}}
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    {{/if}}
  </li>
{{/each}}

components/common/navigation-mega-child-:
{{#each children}}
  {{#if children}}
    <div class="mega-nav-child-list-container" data-root-category-id="{{id}}">
      <span class="mega-nav-child-list-back">
         {{> svg/arrow-down}} <a href="#" data-mega-nav-find-parent>Back</a>
      </span>
      <h2 class="mega-nav-child-header">{{name}}</h2>
      <div class="mega-nav-child-list-inner-container">
        <ul class="mega-nav-child-list" >
          <li class="mega-nav-child-item"><a href="{{url}}">All {{name}}</a></li>
          {{#each children}}
            {{#if children}}
              <li class="mega-nav-child-item" data-mega-nav-child-id="{{id}}" data-mega-nav-find-child>
                <a href="{{url}}">{{name}}</a>
                <span class="mega-nav-arrow">{{> svg/arrow-down}}</span>
              </li>
            {{else}}
              <li class="mega-nav-child-item"><a href="{{url}}">{{name}}</a></li>
            {{/if}}
          {{/each}}
        </ul>
      </div>
    </div>
    {{> components/common/navigation-mega-child-recursive}}
  {{/if}}
{{/each}}

components/header/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-selector dropdown{{#if settings.account_creation_enabled}} accounts-enabled{{/if}}">
    <a class="dropdown-toggle top-bar-button" href="#">
      <span class="currency-selector-active button-text">{{currency_selector.active_currency_code}}</span>
      <span class="currency-selector-flag">{{{currency_selector.active_currency_flag}}}</span>
      {{> svg/arrow-down}}
    </a>
    <ul class="dropdown-panel" tabindex="-1">
      {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a href="{{switch_url}}">
              {{#if is_active}}
                <strong>{{name}}</strong>
              {{else}}
                {{name}}
              {{/if}}
            </a>
        </li>
      {{/each}}
    </ul>
  </div>
{{/if}}

components/header/shipping-messages:
{{#if shipping_messages}}
  {{#each shipping_messages}}
    <div class="promo-message">{{{message}}}</div>
  {{/each}}
{{/if}}

{{#if cart.status_messages}}
  {{#each cart.status_messages}}
    {{#if type '==' 2}}
      <div class="promo-message">{{{message}}}</div>
    {{/if}}
  {{/each}}
{{/if}}

{{#if product.shipping_messages}}
  {{#each product.shipping_messages}}
    <div class="promo-message">{{{message}}}</div>
  {{/each}}
{{/if}}

components/header/site-navigation-left-a:
{{> components/header/site-branding}}

{{> components/header/site-navigation show_search=false}}

<div class="search-button-wrap">
  <button class="button-search-toggle">
    {{> svg/search}}
  </button>
</div>
