pages/product:
---
product:
  reviews:
    limit: {{theme_settings.product-single-reviews-count}}
  videos:
    limit: {{theme_settings.product-single-videos-count}}
  related_products:
    limit: {{theme_settings.product-single-related-count}}
brands: true
---

{{#partial "page"}}
  {{> components/products/product-single-details
    cart_icon=theme_settings.cart_icon
  }}
  {{> components/products/product-single-tabs}}

  {{#if product.related_products.length}}
    <section class="products-related section">
      <div class="container">
        <h3 class="section-title">{{lang 'product.related_products_heading'}}</h3>
        <div class="product-grid product-carousel">
          {{#each product.related_products}}
            {{> components/products/product-grid-item
              this
              show_rating=../settings.show_product_rating
              default_image=../theme_settings.default-product-image
            }}
          {{/each}}
        </div>
      </div>
    </section>
  {{/if}}

  {{> core/structured-data/product}}
  {{> core/structured-data/product-reviews}}

{{/partial}}

{{> layout/base}}

components/products/product-grid-item:
{{!--
  Product Grid Item - As displayed within a grid listing of products.

  @param product object
    The product object to display, handed over from the loop

  @param show_compare bool
    Whether to show "compare" checkboxes

  @param not_lazy bool
    Do not lazy load the images

  @param quick_shop theme-setting
    Theme setting to enable / disable quickshop

  @param image_ratio theme-setting
    The image ratio for this grid

  @param default theme-setting
    The default image if none exists

  @param show_rating platform-setting
    Platform setting to toggle ratings

  @param as_low_as theme-setting
    The setting to show/hide as low as price wording
--}}

<article class="product-grid-item product-block" data-product-title="{{name}}" data-product-id="{{id}}">
  <figure class="product-item-thumbnail">
    {{#if price.saved}}
      {{#unless out_of_stock_message}}
        <div class="product-badge">{{lang 'product.sale'}}</div>
      {{/unless}}
    {{/if}}
    {{#if out_of_stock_message}}
      <div class="product-badge sold-out"><span class="message">{{out_of_stock_message}}</span></div>
    {{/if}}
    <a href="{{url}}">
      {{> components/common/image
        img=image
        url=false
        size="thumb"
        default=default_image
        image_ratio=image_ratio
        not_lazy=not_lazy
      }}
      <div class="product-item-mask">
        <a href="{{url}}" class="item-link"></a>
        <div class="product-item-actions">
          {{#if show_rating}}
            {{#if rating}}
              <p class="product-item-rating">
                <span class="rating-title">{{rating}} {{lang 'product.stars'}}</span>
                {{> components/products/ratings rating=rating}}
              </p>
            {{/if}}

            {{{snippet 'product_rating'}}}
          {{/if}}

          {{#if quick_shop}}
            <button
              class="button button-secondary button-short"
              data-quick-shop
              data-product-id="{{id}}"
            >
              {{lang 'product.quick_view'}}
            </button>
          {{else}}
            <a href="{{url}}" class="button button-secondary button-short">
              {{lang 'product.view_product'}}
            </a>
          {{/if}}

          {{#if show_compare}}
            <label class="button button-short form-label product-item-compare" for="compare-{{id}}">
              {{> components/common/compare-checkbox}}
              <span class="form-label-text">
                {{lang "product.compare"}}
                <svg class="icon icon-check-mark"><use xlink:href="#icon-check-mark" /></svg>
              </span>
            </label>
          {{/if}}
        </div>
      </div>
    </a>
  </figure>

  <div class="product-item-details">
    {{#if brand.name}}
      <div class="product-item-brand">{{brand.name}}</div>
    {{/if}}
    <h5 class="product-item-title">
      <a href="{{url}}" alt="{{name}}">{{name}}</a>
    </h5>

    <div class="product-item-price" data-product-price="{{price.without_tax.formatted}}">
      {{> components/products/price
        price=price
        as_low_as=as_low_as
      }}
    </div>
  </div>
</article>

core/structured-data/product:
{{!-- Product: include in product.html page template --}}

<script id="schema-product">

  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org/",
    "@type": "Product",
    "name": "{{product.title}}",
    "image": "{{getImage product.main_image}}",
    "description": "{{json product.description}}",
    "sku": "{{product.sku}}",
    "brand": {
      "@type": "Thing",
      "name": "{{product.brand.name}}"
    },
    {{#if product.reviews.list}}
      "aggregateRating": {
        "@type": "AggregateRating",
        "ratingValue": "{{product.rating}}",
        "reviewCount": "{{product.num_reviews}}"
      },
    {{/if}}
    "offers": {
      "@type": "Offer",
      "priceCurrency": "{{currency_selector.active_currency_code}}",
      "price": "{{#if product.price.without_tax}}{{product.price.without_tax.value}}{{/if}}{{#if product.price.with_tax}}{{#unless product.price.without_tax}}{{product.price.with_tax.value}}{{/unless}}{{/if}}",
      "itemCondition": "{{#if product.condition '===' 'New'}}http://schema.org/NewCondition{{/if}}{{#if product.condition '===' 'Used'}}http://schema.org/UsedCondition{{/if}}{{#if product.condition '===' 'Refurbished'}}http://schema.org/RefurbishedCondition{{/if}}",
      "availability": "{{#unless product.out_of_stock_message}}http://schema.org/InStock{{else}}http://schema.org/OutOfStock{{/unless}}",
      "seller": {
        "@type": "Organization",
        "name": "{{settings.store_name}}"
      }
    }
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-product');
  script.parentElement.removeChild(script);
</script>

core/structured-data/product-reviews:
{{!-- Product Reviews: include in product.html page template --}}

{{#if settings.show_product_reviews}}
  {{#each product.reviews.list}}
    <script type="application/ld+json">
      {
        "@context": "http://schema.org/",
        "@type": "Review",
        "itemReviewed": {
          "@type": "Product",
          "image": "{{getImage ../product.main_image}}",
          "name": "{{../product.title}}"
        },
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "{{rating}}"
        },
        "name": "{{title}}",
        "author": {
          "@type": "Person",
          "name": "{{name}}"
        },
        "reviewBody": "{{text}}",
        "publisher": {
          "@type": "Organization",
          "name": "{{../settings.store_name}}"
        }
      }
    </script>
  {{/each}}
{{/if}}

components/products/price:
{{#if price}}
  {{#or has_options product.options.length}}
    <div>

      {{#if price.without_tax}}
        <div class="product-price-line" data-product-price-wrapper="without-tax">
          {{#if page_type '!==' 'product'}}
            {{#if as_low_as}}
              <span class="as-low-as-pricing">{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}

          {{#if price.saved}}
            <span class="price-rrp {{#unless price.with_tax}}highlight{{/unless}}">{{price.rrp_without_tax.formatted}}</span>
          {{/if}}

          <span class="price-value"> {{price.without_tax.formatted}}</span>

          {{#if price.with_tax}}
            <span class="tax-label"> {{lang 'product.excluding_tax'}}</span>
          {{/if}}
        </div>
      {{/if}}

      {{#if price.with_tax}}

        <div class="product-price-line{{#if price.without_tax}} price-second-line{{/if}}" data-product-price-wrapper="with-tax">
          {{#if page_type '!==' 'product'}}
            {{#if as_low_as}}
              <span class="as-low-as-pricing">{{lang 'product.as_low_as_price_message'}}</span>
            {{/if}}
          {{/if}}

          {{#if price.saved}}
            <span class="price-rrp {{#unless price.without_tax}}highlight{{/unless}}">{{price.rrp_with_tax.formatted}}</span>
          {{/if}}

          <span class="price-value"> {{price.with_tax.formatted}}</span>

          {{#if price.without_tax}}
            <span class="tax-label"> {{lang 'product.including_tax'}}</span>
          {{/if}}
        </div>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <div class="price-you-save" data-product-price-saved>
            {{lang 'product.you_save'}} {{price.saved.formatted}}
          </div>
        {{/if}}
      {{/if}}
    </div>
  {{else}}
    <div>
      {{#if price.without_tax}}

        <div class="product-price-line" data-product-price-wrapper="without-tax">
          {{#if price.saved}}
            <span class="price-rrp {{#unless price.with_tax}}highlight{{/unless}}">{{price.rrp_without_tax.formatted}}</span>
          {{/if}}

          <span class="price-value"> {{price.without_tax.formatted}}</span>

          {{#if price.with_tax}}
            <span class="tax-label"> {{lang 'product.excluding_tax'}}</span>
          {{/if}}
        </div>

      {{/if}}

      {{#if price.with_tax}}

        <div class="product-price-line{{#if price.without_tax}} price-second-line{{/if}}" data-product-price-wrapper="with-tax">
          {{#if price.saved}}
            <span class="price-rrp {{#unless price.without_tax}}highlight{{/unless}}">{{price.rrp_with_tax.formatted}}</span>
          {{/if}}

          <span class="price-value"> {{price.with_tax.formatted}}</span>

          {{#if price.without_tax}}
            <span class="tax-label"> {{lang 'product.including_tax'}}</span>
          {{/if}}
        </div>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <div class="price-you-save" data-product-price-saved>
            {{lang 'product.you_save'}} {{price.saved.formatted}}
          </div>
        {{/if}}
      {{/if}}
    </div>
  {{/or}}
{{/if}}

