components/common/share-buttons:
{{#if share_buttons}}
  <nav class="share-buttons-nav">
    <ul class="share-buttons" data-share-buttons>

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook'}}
          <li class="social-link facebook">
            <a target="_blank" href="http://www.facebook.com/sharer.php?u={{../../url}}" class="facebook">
              {{> svg/social/facebook}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'twitter'}}
          <li class="social-link twitter">
            <a target="_blank" href="https://twitter.com/share?url={{../../url}}&amp;text={{../../title}}" class="twitter">
              {{> svg/social/twitter}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'google_plusone'}}
          <li class="social-link google-plus">
            <a target="_blank" href="https://plus.google.com/share?url={{../../url}}" class="google">
              {{> svg/social/googleplus}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'linkedin'}}
          <li class="social-link linkedin">
            <a target="_blank" href="http://www.linkedin.com/shareArticle?url={{../../url}}&amp;title={{../../title}}" class="linkedin">
              {{> svg/social/linkedin}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'email'}}
          <li class="social-link mail">
            <a href="mailto:?subject={{../../title}}&amp;body={{../../url}}" class="email">
              {{> svg/social/email}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'print'}}
          <li class="social-link print">
            <a class="print" href="javascript:;" onclick="window.print()">
              {{> svg/social/print}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'pinterest'}}
          <li class="social-link pinterest">
            <a target="_blank" href="//pinterest.com/pin/create/button/?url={{../../url}}&amp;media={{../../image}}&amp;description={{../../title}}" class="share-pinterest">
              {{> svg/social/pinterest}}
            </a>
          </li>
        {{/if}}
      {{/each}}

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook_like'}}
          <li class="social-link facebook-like">
            <div class="fb-like"
              data-href="{{../../url}}"
              data-layout="button_count"
              data-action="like"
              data-show-faces="false">
            </div>
          </li>
        {{/if}}
      {{/each}}

    </ul>
  </nav>
{{/if}}

core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

components/common/navigation-mega:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

{{#each nav_items}}
  <li class="parent {{#if children}}has-children tier-dropdown{{/if}}">
    <a href="{{url}}">{{name}}</a>
    {{#if children}}
      <ul class="children">
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul>
                {{#each children}}
                  {{> components/common/site-navigation-submenus}}
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    {{/if}}
  </li>
{{/each}}

components/common/navigation-mega-child-:
{{#each children}}
  {{#if children}}
    <div class="mega-nav-child-list-container" data-root-category-id="{{id}}">
      <span class="mega-nav-child-list-back">
         {{> svg/arrow-down}} <a href="#" data-mega-nav-find-parent>Back</a>
      </span>
      <h2 class="mega-nav-child-header">{{name}}</h2>
      <div class="mega-nav-child-list-inner-container">
        <ul class="mega-nav-child-list" >
          <li class="mega-nav-child-item"><a href="{{url}}">All {{name}}</a></li>
          {{#each children}}
            {{#if children}}
              <li class="mega-nav-child-item" data-mega-nav-child-id="{{id}}" data-mega-nav-find-child>
                <a href="{{url}}">{{name}}</a>
                <span class="mega-nav-arrow">{{> svg/arrow-down}}</span>
              </li>
            {{else}}
              <li class="mega-nav-child-item"><a href="{{url}}">{{name}}</a></li>
            {{/if}}
          {{/each}}
        </ul>
      </div>
    </div>
    {{> components/common/navigation-mega-child-recursive}}
  {{/if}}
{{/each}}
