components/common/image:
{{!--
  Helper to display any image with alt text.

  @param img          The image object
  @param size         The preset size to use.
  @param url          Optional: Set the url this image should link to.
  @param default      Default image to display
  @param image_ratio  The image class to apply to the wrapping element.
  @param image_size  The image class to apply to the wrapping element.
  @param not_lazy     Optional: do not lazy load (mainly used for ajax-loaded images)
--}}

{{#if not_lazy}}
  {{#if url}}
    <a
      class="replaced-image {{image_ratio}}"
      href="{{url}}"
      style="background-image: url({{getImage img size (cdn default)}});">
      <img src="{{getImage img size (cdn default)}}" alt="{{img.alt}}">
    </a>
  {{else}}
    <div
      class="replaced-image {{image_ratio}}"
      style="background-image: url({{getImage img size (cdn default)}});">
      <img src="{{getImage img size (cdn default)}}" alt="{{img.alt}}">
    </div>
  {{/if}}
{{else}}
  {{#if url}}
    <a
      class="lazy-image replaced-image {{image_ratio}}"
      href="{{url}}"
      data-src="{{getImage img size (cdn default)}}">
      <img class="lazy-image" data-src="{{getImage img size (cdn default)}}" alt="{{img.alt}}">
      <div class="spinner">
        <svg class="icon icon-spinner">
          <use xlink:href="#icon-spinner" />
        </svg>
      </div>
    </a>
  {{else}}
    <div
      class="lazy-image replaced-image {{image_ratio}}"
      data-src="{{getImage img size (cdn default)}}">
      <img class="lazy-image" data-src="{{getImage img size (cdn default)}}" alt="{{img.alt}}">
      <div class="spinner">
        <svg class="icon icon-spinner">
          <use xlink:href="#icon-spinner" />
        </svg>
      </div>
    </div>
  {{/if}}
{{/if}}

components/blog/blog-post-meta:
{{!
  @param date_published:
         > blog.posts.date_published
         > blog.post.date_published
  @param author
         > blog.posts.author
         > blog.post.author
}}

<p class="post-meta">
  <svg class="icon icon-calendar"><use xlink:href="#icon-calendar" /></svg>
  <time class="post-date">{{date_published}}</time>

  {{#if author.length}}
    <svg class="icon icon-avatar"><use xlink:href="#icon-avatar" /></svg>
    <span class="post-author">{{author}}</span>
  {{/if}}
</p>

components/common/share-buttons:
{{#if share_buttons}}
  <nav class="share-buttons-nav">
    <ul class="share-buttons" data-share-buttons>

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook'}}
          <li class="social-link facebook">
            <a target="_blank" href="http://www.facebook.com/sharer.php?u={{../../url}}" class="facebook">
              {{> svg/social/facebook}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'twitter'}}
          <li class="social-link twitter">
            <a target="_blank" href="https://twitter.com/share?url={{../../url}}&amp;text={{../../title}}" class="twitter">
              {{> svg/social/twitter}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'google_plusone'}}
          <li class="social-link google-plus">
            <a target="_blank" href="https://plus.google.com/share?url={{../../url}}" class="google">
              {{> svg/social/googleplus}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'linkedin'}}
          <li class="social-link linkedin">
            <a target="_blank" href="http://www.linkedin.com/shareArticle?url={{../../url}}&amp;title={{../../title}}" class="linkedin">
              {{> svg/social/linkedin}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'email'}}
          <li class="social-link mail">
            <a href="mailto:?subject={{../../title}}&amp;body={{../../url}}" class="email">
              {{> svg/social/email}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'print'}}
          <li class="social-link print">
            <a class="print" href="javascript:;" onclick="window.print()">
              {{> svg/social/print}}
            </a>
          </li>
        {{/if}}

        {{#if service '===' 'pinterest'}}
          <li class="social-link pinterest">
            <a target="_blank" href="//pinterest.com/pin/create/button/?url={{../../url}}&amp;media={{../../image}}&amp;description={{../../title}}" class="share-pinterest">
              {{> svg/social/pinterest}}
            </a>
          </li>
        {{/if}}
      {{/each}}

      {{#each share_buttons.buttons}}
        {{#if service '===' 'facebook_like'}}
          <li class="social-link facebook-like">
            <div class="fb-like"
              data-href="{{../../url}}"
              data-layout="button_count"
              data-action="like"
              data-show-faces="false">
            </div>
          </li>
        {{/if}}
      {{/each}}

    </ul>
  </nav>
{{/if}}
