components/common/navigation:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

<nav class="navigation">
  <ul>
    {{#each nav_items}}
      <li class="parent {{#if children}}tier-dropdown{{/if}}">
        <a class="top-level-nav-link {{#if children}}tier-toggle{{/if}}" href="{{url}}">{{name}}</a>
        {{#if children}}
          <ul class="tier-panel">
            <li class="parent-all"><a href="{{url}}">{{lang 'common.navigation_all'}} {{name}}</a></li>
            {{#each children}}
              <li {{#if children}}class="tier-dropdown"{{/if}}>
                <a {{#if children}}class="tier-toggle"{{/if}} href="{{url}}">{{name}}</a>
                {{#if children}}
                  <ul class="tier-panel">
                    <li class="parent-all"><a href="{{url}}">{{lang 'common.navigation_all'}} {{name}}</a></li>
                    {{#each children}}
                      {{> components/common/site-navigation-submenus}}
                    {{/each}}
                  </ul>
                {{/if}}
              </li>
            {{/each}}
          </ul>
        {{/if}}
      </li>
    {{/each}}
    {{#if settings.gift_certificates_enabled}}
      {{#if nav_items '==' pages}}
        <li><a href="{{urls.gift_certificate.purchase}}">{{lang 'header.gift_certificate_cta'}}</a></li>
      {{/if}}
    {{/if}}
  </ul>
</nav>

components/common/navigation-mega:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

{{#each nav_items}}
  <li class="parent {{#if children}}has-children tier-dropdown{{/if}}">
    <a href="{{url}}">{{name}}</a>
    {{#if children}}
      <ul class="children">
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul>
                {{#each children}}
                  {{> components/common/site-navigation-submenus}}
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    {{/if}}
  </li>
{{/each}}

components/common/navigation-mega-child-:
{{#each children}}
  {{#if children}}
    <div class="mega-nav-child-list-container" data-root-category-id="{{id}}">
      <span class="mega-nav-child-list-back">
         {{> svg/arrow-down}} <a href="#" data-mega-nav-find-parent>Back</a>
      </span>
      <h2 class="mega-nav-child-header">{{name}}</h2>
      <div class="mega-nav-child-list-inner-container">
        <ul class="mega-nav-child-list" >
          <li class="mega-nav-child-item"><a href="{{url}}">All {{name}}</a></li>
          {{#each children}}
            {{#if children}}
              <li class="mega-nav-child-item" data-mega-nav-child-id="{{id}}" data-mega-nav-find-child>
                <a href="{{url}}">{{name}}</a>
                <span class="mega-nav-arrow">{{> svg/arrow-down}}</span>
              </li>
            {{else}}
              <li class="mega-nav-child-item"><a href="{{url}}">{{name}}</a></li>
            {{/if}}
          {{/each}}
        </ul>
      </div>
    </div>
    {{> components/common/navigation-mega-child-recursive}}
  {{/if}}
{{/each}}
